import React, { useState, useEffect, useRef, useCallback } from 'react';
import './Race.css';

const WS_URL = process.env.REACT_APP_WS_URL || `ws://${window.location.hostname}:5001`;

// Utility: calcola distanza Haversine (m)
function haversine(lat1, lon1, lat2, lon2) {
  const R = 6371000;
  const toRad = d => d * Math.PI / 180;
  const dLat = toRad(lat2 - lat1);
  const dLon = toRad(lon2 - lon1);
  const a = Math.sin(dLat / 2) ** 2 + 
            Math.cos(toRad(lat1)) * Math.cos(toRad(lat2)) * Math.sin(dLon / 2) ** 2;
  return R * 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
}

// Trova il settore pi√π vicino
function findClosestSector(lat, lon, sectors) {
  let minDist = Infinity;
  let closestIdx = 0;
  sectors.forEach((s, i) => {
    const d = haversine(lat, lon, s.lat, s.lon);
    if (d < minDist) {
      minDist = d;
      closestIdx = i;
    }
  });
  return closestIdx;
}

export default function RaceLive({ raceConfig, onStopRace }) {
  const { circuitData, totalLaps, assignments, pilots } = raceConfig;

  const [driversState, setDriversState] = useState({});
  const [leaderboard, setLeaderboard] = useState([]);
  const [activeBattles, setActiveBattles] = useState([]);

  const canvasRef = useRef(null);
  const wsRef = useRef(null);
  const animFrameRef = useRef(null);

  // ========== WebSocket GPS ==========
  useEffect(() => {
    const ws = new WebSocket(WS_URL);
    wsRef.current = ws;

    ws.onopen = () => console.log('[RaceLive] WebSocket connesso');

    ws.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data);

        // Formato: il backend invia { mac, lat, lon, speedKmh, ... }
        if (data.mac && data.lat && data.lon) {
          const mac = data.mac;

          // Ignora device non assegnati
          if (!assignments[mac]) return;

          const gps = {
            mac,
            lat: data.lat,
            lon: data.lon,
            speed: data.speedKmh || 0,
            ts: data.ts
          };

          handleGPSUpdate(gps);
        }
      } catch (e) {
        console.error('[WS] Parse error:', e);
      }
    };

    ws.onerror = (err) => console.error('[WS] Errore:', err);
    ws.onclose = () => console.log('[WS] Chiuso');

    return () => {
      if (ws.readyState === WebSocket.OPEN) ws.close();
    };
  }, [assignments]);

  // ========== Gestione GPS Update ==========
  const handleGPSUpdate = useCallback((gps) => {
    if (!circuitData?.sectors) return;

    const { mac, lat, lon, speed } = gps;
    const sectorIdx = findClosestSector(lat, lon, circuitData.sectors);
    const totalSectors = circuitData.sectors.length;

    // Trova pilota assegnato
    const pilotId = assignments[mac];
    const pilot = pilots.find(p => p.id === pilotId);
    if (!pilot) return;

    setDriversState(prev => {
      const existing = prev[mac] || {
        mac,
        name: `${pilot.name} ${pilot.surname}`,
        team: pilot.team,
        color: pilot.color || `hsl(${Math.random() * 360}, 75%, 55%)`,
        lat,
        lon,
        speed: 0,
        sectorIdx,
        lapCount: 0,
        lastLapTime: null,
        bestLapTime: null,
        lastSectorIdx: sectorIdx,
        lapStartTime: Date.now(),
        trail: [],
      };

      // Rilevamento completamento giro
      let newLapCount = existing.lapCount;
      let newLapTime = existing.lastLapTime;
      let newBestLap = existing.bestLapTime;

      if (existing.lastSectorIdx > totalSectors - 10 && sectorIdx < 10) {
        const lapDuration = (Date.now() - existing.lapStartTime) / 1000;
        if (lapDuration > 5) {
          newLapCount = existing.lapCount + 1;
          newLapTime = lapDuration;
          if (!newBestLap || lapDuration < newBestLap) {
            newBestLap = lapDuration;
          }
          console.log(`[LAP] ${existing.name} completato giro ${newLapCount} in ${lapDuration.toFixed(2)}s`);
        }
      }

      const newTrail = [...existing.trail, { lat, lon }].slice(-20);

      return {
        ...prev,
        [mac]: {
          ...existing,
          lat,
          lon,
          speed,
          sectorIdx,
          lastSectorIdx: sectorIdx,
          lapCount: newLapCount,
          lastLapTime: newLapTime,
          bestLapTime: newBestLap,
          lapStartTime: newLapCount > existing.lapCount ? Date.now() : existing.lapStartTime,
          trail: newTrail,
          updatedAt: Date.now(),
        }
      };
    });
  }, [circuitData, assignments, pilots]);

  // ========== Aggiorna classifica e battaglie ==========
  useEffect(() => {
    const sorted = Object.values(driversState).sort((a, b) => {
      if (a.lapCount !== b.lapCount) return b.lapCount - a.lapCount;
      return b.sectorIdx - a.sectorIdx;
    });
    setLeaderboard(sorted);

    // Battaglie
    const battles = [];
    for (let i = 0; i < sorted.length - 1; i++) {
      const driver1 = sorted[i];
      const driver2 = sorted[i + 1];

      if (driver1.lapCount === driver2.lapCount) {
        const dist = haversine(driver1.lat, driver1.lon, driver2.lat, driver2.lon);
        if (dist < 20) {
          battles.push({
            leader: driver1,
            chaser: driver2,
            distance: dist,
            position: i + 1
          });
        }
      }
    }
    setActiveBattles(battles);
  }, [driversState]);

  // ========== Rendering Canvas ==========
  useEffect(() => {
    if (!circuitData || !canvasRef.current) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const dpr = window.devicePixelRatio || 1;

    const resize = () => {
      const rect = canvas.parentElement.getBoundingClientRect();
      canvas.width = rect.width * dpr;
      canvas.height = rect.height * dpr;
      canvas.style.width = `${rect.width}px`;
      canvas.style.height = `${rect.height}px`;
      ctx.scale(dpr, dpr);
    };
    resize();
    window.addEventListener('resize', resize);

    // Calcola bounds pista
    const lats = circuitData.sectors.map(s => s.lat);
    const lons = circuitData.sectors.map(s => s.lon);
    const minLat = Math.min(...lats);
    const maxLat = Math.max(...lats);
    const minLon = Math.min(...lons);
    const maxLon = Math.max(...lons);
    const padding = 50;

    const project = (lat, lon) => {
      const rect = canvas.getBoundingClientRect();
      const w = rect.width - padding * 2;
      const h = rect.height - padding * 2;
      const x = ((lon - minLon) / (maxLon - minLon)) * w + padding;
      const y = h - ((lat - minLat) / (maxLat - minLat)) * h + padding;
      return { x, y };
    };

    const draw = () => {
      const rect = canvas.getBoundingClientRect();
      ctx.clearRect(0, 0, rect.width, rect.height);

      // Sfondo
      ctx.fillStyle = '#0a0e1a';
      ctx.fillRect(0, 0, rect.width, rect.height);

      // Tracciato
      ctx.strokeStyle = 'rgba(255, 255, 255, 0.3)';
      ctx.lineWidth = 30;
      ctx.lineCap = 'round';
      ctx.lineJoin = 'round';
      ctx.beginPath();
      circuitData.sectors.forEach((s, i) => {
        const p = project(s.lat, s.lon);
        i === 0 ? ctx.moveTo(p.x, p.y) : ctx.lineTo(p.x, p.y);
      });
      ctx.closePath();
      ctx.stroke();

      // Linea centrale
      ctx.strokeStyle = 'rgba(255, 215, 0, 0.5)';
      ctx.lineWidth = 2;
      ctx.setLineDash([10, 10]);
      ctx.beginPath();
      circuitData.sectors.forEach((s, i) => {
        const p = project(s.lat, s.lon);
        i === 0 ? ctx.moveTo(p.x, p.y) : ctx.lineTo(p.x, p.y);
      });
      ctx.closePath();
      ctx.stroke();
      ctx.setLineDash([]);

      // Finish line
      if (circuitData.sectors.length > 1) {
        const s0 = project(circuitData.sectors[0].lat, circuitData.sectors[0].lon);
        const s1 = project(circuitData.sectors[1].lat, circuitData.sectors[1].lon);
        const angle = Math.atan2(s1.y - s0.y, s1.x - s0.x);
        const perpAngle = angle + Math.PI / 2;
        const flagWidth = 30;

        ctx.save();
        ctx.translate(s0.x, s0.y);
        ctx.rotate(perpAngle);

        // Bandiera a scacchi
        const cols = 6;
        const rows = 4;
        const cellW = flagWidth / cols;
        const cellH = flagWidth / rows;

        for (let r = 0; r < rows; r++) {
          for (let c = 0; c < cols; c++) {
            ctx.fillStyle = (r + c) % 2 === 0 ? '#fff' : '#000';
            ctx.fillRect(c * cellW - flagWidth / 2, r * cellH - flagWidth / 2, cellW, cellH);
          }
        }
        ctx.restore();
      }

      // Piloti
      const sortedDrivers = Object.values(driversState).sort((a, b) => {
        if (a.lapCount !== b.lapCount) return a.lapCount - b.lapCount;
        return a.sectorIdx - b.sectorIdx;
      });

      sortedDrivers.forEach((driver, idx) => {
        const pos = project(driver.lat, driver.lon);

        // Trail
        if (driver.trail && driver.trail.length > 1) {
          ctx.strokeStyle = driver.color + '40';
          ctx.lineWidth = 4;
          ctx.beginPath();
          driver.trail.forEach((t, ti) => {
            const tp = project(t.lat, t.lon);
            ti === 0 ? ctx.moveTo(tp.x, tp.y) : ctx.lineTo(tp.x, tp.y);
          });
          ctx.stroke();
        }

        // Driver dot
        const isLeader = idx === sortedDrivers.length - 1;
        const radius = isLeader ? 7 : 6;

        ctx.fillStyle = driver.color;
        ctx.beginPath();
        ctx.arc(pos.x, pos.y, radius, 0, Math.PI * 2);
        ctx.fill();

        ctx.strokeStyle = '#fff';
        ctx.lineWidth = 2;
        ctx.stroke();

        // Nome
        ctx.fillStyle = '#fff';
        ctx.font = 'bold 12px Roboto Mono';
        ctx.textAlign = 'center';
        ctx.fillText(driver.name.split(' ')[0], pos.x, pos.y - 15);
      });

      animFrameRef.current = requestAnimationFrame(draw);
    };

    draw();

    return () => {
      window.removeEventListener('resize', resize);
      if (animFrameRef.current) cancelAnimationFrame(animFrameRef.current);
    };
  }, [circuitData, driversState]);

  // Formatta tempo
  const formatLapTime = (seconds) => {
    if (!seconds) return '--:--.---';
    const mins = Math.floor(seconds / 60);
    const secs = (seconds % 60).toFixed(3);
    return `${mins}:${secs.padStart(6, '0')}`;
  };

  return (
    <div className="race-page">
      <header className="race-header">
        <div className="race-header-left">
          <div className="race-logo">üèÅ RACESENSE</div>
          <div className="race-title">LIVE RACE</div>
        </div>
        <div className="race-header-right">
          <div className="circuit-badge">
            <span className="circuit-icon">üõ£Ô∏è</span>
            <span className="circuit-name">{circuitData.name}</span>
          </div>
          <div className="laps-badge">
            <span className="laps-icon">üèÅ</span>
            <span className="laps-text">{totalLaps} GIRI</span>
          </div>
        </div>
      </header>

      <div className="race-live">
        <div className="race-main">
          <div className="track-container">
            <canvas ref={canvasRef} className="track-canvas" />

            <div className="track-overlay">
              <div className="live-indicator">
                <span className="live-dot"></span>
                LIVE
              </div>

              {circuitData && (
                <div className="track-info">
                  <div className="track-stat">
                    <span className="stat-icon">üìè</span>
                    <span className="stat-text">{circuitData.stats?.lengthMeters?.toFixed(0)}m</span>
                  </div>
                  <div className="track-stat">
                    <span className="stat-icon">üìç</span>
                    <span className="stat-text">{circuitData.sectors?.length || 0} sectors</span>
                  </div>
                </div>
              )}
            </div>
          </div>

          <aside className="leaderboard">
            <div className="leaderboard-header">
              <h3>üèÜ CLASSIFICA</h3>
              <div className="leaderboard-subtitle">
                {leaderboard.length} Piloti
              </div>
            </div>

            <div className="leaderboard-list">
              {leaderboard.length === 0 ? (
                <div className="no-drivers">
                  <p>‚è≥ In attesa di dati GPS...</p>
                </div>
              ) : (
                leaderboard.map((driver, pos) => {
                  const battle = activeBattles.find(b => b.leader.mac === driver.mac);

                  return (
                    <React.Fragment key={driver.mac}>
                      <div className={`leaderboard-item ${pos === 0 ? 'leader' : ''}`}>
                        <div className="position">{pos + 1}</div>

                        <div className="driver-color" style={{ background: driver.color }}></div>

                        <div className="driver-info">
                          <div className="driver-name">
                            {driver.name}
                            {pos === 0 && <span className="leader-badge">üëë</span>}
                          </div>
                          <div className="driver-stats">
                            <span className="lap-count">Giro {driver.lapCount}/{totalLaps}</span>
                            <span className="sector">S{driver.sectorIdx}</span>
                          </div>
                        </div>

                        <div className="driver-times">
                          {pos === 0 ? (
                            <div className="gap leader-gap">LEADER</div>
                          ) : (() => {
                            const leader = leaderboard[0];
                            let gapDisplay = '';

                            if (driver.lapCount < leader.lapCount) {
                              const lapDiff = leader.lapCount - driver.lapCount;
                              gapDisplay = `+${lapDiff} ${lapDiff === 1 ? 'LAP' : 'LAPS'}`;
                            } else {
                              const sectorDiff = leader.sectorIdx - driver.sectorIdx;
                              if (sectorDiff > 0) {
                                const estimatedGap = (sectorDiff / 10).toFixed(1);
                                gapDisplay = `+${estimatedGap}s`;
                              } else {
                                gapDisplay = '+0.0s';
                              }
                            }

                            return <div className="gap">{gapDisplay}</div>;
                          })()}

                          {driver.bestLapTime && (
                            <div className="best-lap">
                              ‚è±Ô∏è {formatLapTime(driver.bestLapTime)}
                            </div>
                          )}
                        </div>
                      </div>

                      {battle && (
                        <div className="battle-inline">
                          <div className="battle-line"></div>
                          <div className="battle-info">
                            <span className="battle-icon">‚öîÔ∏è</span>
                            <span className="battle-gap">{battle.distance.toFixed(1)}m</span>
                            <span className="battle-speed">
                              {(battle.chaser.speed - battle.leader.speed) > 0 ? '‚Üë' : '‚Üì'}
                            </span>
                          </div>
                          <div className="battle-line"></div>
                        </div>
                      )}
                    </React.Fragment>
                  );
                })
              )}
            </div>
          </aside>
        </div>

        <div className="race-controls">
          <button className="stop-race-btn" onClick={onStopRace}>
            ‚èπÔ∏è Termina Gara
          </button>
        </div>
      </div>
    </div>
  );
}
