import React, { useState, useEffect } from "react";

export default function Championships() {
    const [name, setName] = useState("");
    const [photo, setPhoto] = useState(null);
    const [championships, setChampionships] = useState([]);
    const [uploadPreview, setUploadPreview] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");

    // === Carica campionati salvati ===
    useEffect(() => {
        fetch("/api/championships")
            .then((res) => res.json())
            .then(setChampionships)
            .catch(() => setChampionships([]));
    }, []);

    // === Gestione immagine ===
    const handlePhotoChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            setPhoto(file);
            const reader = new FileReader();
            reader.onloadend = () => setUploadPreview(reader.result);
            reader.readAsDataURL(file);
        }
    };

    // === Aggiungi nuovo campionato ===
    const handleAddChampionship = async (e) => {
        e.preventDefault();
        if (!name.trim()) return setError("Inserisci un nome per il campionato.");
        setError("");
        setLoading(true);

        const formData = new FormData();
        formData.append("name", name);
        if (photo) formData.append("photo", photo);

        try {
            const res = await fetch("/api/championships", {
                method: "POST",
                body: formData,
            });
            const data = await res.json();
            setChampionships(data);
            setName("");
            setPhoto(null);
            setUploadPreview(null);
        } catch (err) {
            console.error(err);
            setError("Errore nel salvataggio del campionato.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="main small-top">
            <h1 className="section-title">Campionati</h1>
            <p className="subtitle">
                Crea e gestisci i tuoi campionati. Puoi assegnare pi√π gare ad ogni
                campionato.
            </p>

            {/* === FORM CREAZIONE === */}
            <form className="pilot-form" onSubmit={handleAddChampionship}>
                <div className="form-row">
                    <div className="form-col">
                        <label>Nome del Campionato *</label>
                        <input
                            type="text"
                            className="input"
                            value={name}
                            placeholder="Es. Formula GP Italia"
                            onChange={(e) => setName(e.target.value)}
                        />
                    </div>

                    <div className="form-col">
                        <label>Foto (opzionale)</label>
                        <input
                            type="file"
                            accept="image/*"
                            onChange={handlePhotoChange}
                            className="input"
                        />
                    </div>

                    <div className="form-col" style={{ display: "flex", alignItems: "end" }}>
                        <button type="submit" className="btn-primary" disabled={loading}>
                            {loading ? "Salvataggio..." : "Aggiungi Campionato"}
                        </button>
                    </div>
                </div>

                {uploadPreview && (
                    <div className="dz-preview">
                        <img src={uploadPreview} alt="preview" />
                        <span className="dz-filename">{photo?.name}</span>
                    </div>
                )}
                {error && <div className="error">{error}</div>}
            </form>

            {/* === LISTA CAMPIONATI === */}
            <h2 className="section-subtitle">Campionati salvati</h2>
            {championships.length === 0 ? (
                <div className="cr-empty">
                    <div className="cr-empty-emoji">üèÅ</div>
                    <div className="cr-empty-title">Nessun campionato creato</div>
                    <p className="muted">Aggiungine uno sopra per iniziare.</p>
                </div>
            ) : (
                <div className="cr-grid">
                    {championships.map((ch) => (
                        <div key={ch.id} className="cr-card">
                            <div className="cr-card-head">
                                <h3 className="cr-title">{ch.name}</h3>
                            </div>
                            {ch.photo ? (
                                <img
                                    src={ch.photo}
                                    alt={ch.name}
                                    className="cr-mini"
                                    style={{ objectFit: "cover" }}
                                />
                            ) : (
                                <div className="cr-mini"></div>
                            )}
                            <div className="cr-meta">
                                <span className="pill ghost">ID: {ch.id}</span>
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}
