import React, { useEffect, useMemo, useState } from "react";

export default function Championships() {
  const API_BASE =
    process.env.REACT_APP_API_BASE || `http://${window.location.hostname}:5000`;

  // Stato
  const [items, setItems] = useState([]);
  const [loadingList, setLoadingList] = useState(true);

  // Nuovo campeonato
  const [name, setName] = useState("");
  const [photoFile, setPhotoFile] = useState(null);
  const [photoPreview, setPhotoPreview] = useState(null);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState("");

  // UI/filtri
  const [query, setQuery] = useState("");
  const [sortKey, setSortKey] = useState("createdAt_desc"); // name_asc | name_desc | createdAt_desc

  // Gestione formule in-line per card espansa
  const [expanded, setExpanded] = useState({}); // { [champId]: true/false }
  const [newFormula, setNewFormula] = useState({}); // { [champId]: "Formula 2" }
  const [savingFormulaId, setSavingFormulaId] = useState(null);

  // Helpers
  const fetchAll = async () => {
    setLoadingList(true);
    try {
      const r = await fetch(`${API_BASE}/api/championships`);
      const list = await r.json();
      const normalized = (list || []).map((c) => ({
        ...c,
        formulas: Array.isArray(c.formulas) ? c.formulas : [],
      }));
      setItems(normalized);
    } catch {
      setItems([]);
    } finally {
      setLoadingList(false);
    }
  };

  useEffect(() => {
    fetchAll();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [API_BASE]);

  // ===== CREAZIONE CAMPIONATO =====
  const onPickFile = (file) => {
    if (!file) return;
    setPhotoFile(file);
    const rd = new FileReader();
    rd.onloadend = () => setPhotoPreview(rd.result);
    rd.readAsDataURL(file);
  };

  const onDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    const f = e.dataTransfer.files?.[0];
    if (f) onPickFile(f);
  };

  const createChamp = async (e) => {
    e.preventDefault();
    setError("");
    if (!name.trim()) {
      setError("Inserisci il nome del campionato");
      return;
    }
    setSaving(true);
    try {
      const fd = new FormData();
      fd.append("name", name.trim());
      if (photoFile) fd.append("photo", photoFile);

      const r = await fetch(`${API_BASE}/api/championships`, {
        method: "POST",
        body: fd,
      });
      if (!r.ok) throw new Error("Errore HTTP " + r.status);
      const list = await r.json();
      const normalized = (list || []).map((c) => ({
        ...c,
        formulas: Array.isArray(c.formulas) ? c.formulas : [],
      }));
      setItems(normalized);
      setName("");
      setPhotoFile(null);
      setPhotoPreview(null);
    } catch (e2) {
      console.error(e2);
      setError("Errore durante il salvataggio del campionato.");
    } finally {
      setSaving(false);
    }
  };

  // ===== FORMULE =====
  const addFormula = async (champId) => {
    const label = (newFormula[champId] || "").trim();
    if (!label) return;
    setSavingFormulaId(champId);
    try {
      const r = await fetch(
        `${API_BASE}/api/championships/${encodeURIComponent(champId)}/formulas`,
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ label }),
        }
      );
      if (!r.ok) throw new Error("Errore HTTP " + r.status);
      const updated = await r.json();
      setItems((prev) =>
        prev.map((c) => (c.id === updated.id ? { ...updated, formulas: updated.formulas || [] } : c))
      );
      setNewFormula((p) => ({ ...p, [champId]: "" }));
      setExpanded((p) => ({ ...p, [champId]: true }));
    } catch (e) {
      console.error(e);
      alert("Errore durante l'aggiunta della formula");
    } finally {
      setSavingFormulaId(null);
    }
  };

  const deleteFormula = async (champId, formulaId) => {
    if (!window.confirm("Eliminare questa formula?")) return;
    try {
      const r = await fetch(
        `${API_BASE}/api/championships/${encodeURIComponent(
          champId
        )}/formulas/${encodeURIComponent(formulaId)}`,
        { method: "DELETE" }
      );
      if (!r.ok) throw new Error("Errore HTTP " + r.status);
      const updated = await r.json();
      setItems((prev) =>
        prev.map((c) => (c.id === updated.id ? { ...updated, formulas: updated.formulas || [] } : c))
      );
    } catch (e) {
      console.error(e);
      alert("Errore durante l'eliminazione della formula");
    }
  };

  // ===== CHAMPIONATO: DELETE =====
  const deleteChamp = async (champId) => {
    if (!window.confirm("Eliminare il campionato? L‚Äôoperazione non √® reversibile.")) return;
    try {
      const r = await fetch(`${API_BASE}/api/championships/${encodeURIComponent(champId)}`, {
        method: "DELETE",
      });
      if (!r.ok) throw new Error("Errore HTTP " + r.status);
      const list = await r.json(); // ritorniamo la lista aggiornata
      const normalized = (list || []).map((c) => ({
        ...c,
        formulas: Array.isArray(c.formulas) ? c.formulas : [],
      }));
      setItems(normalized);
    } catch (e) {
      console.error(e);
      alert("Errore durante l'eliminazione del campionato");
    }
  };

  // ===== LISTA FILTRATA/ORDINATA =====
  const filtered = useMemo(() => {
    const q = query.trim().toLowerCase();
    let arr = items.filter((c) => {
      if (!q) return true;
      const hay = `${c.name || ""} ${(c.formulas || []).map((f) => f.label).join(" ")}`.toLowerCase();
      return hay.includes(q);
    });
    switch (sortKey) {
      case "name_asc":
        arr.sort((a, b) => a.name.localeCompare(b.name));
        break;
      case "name_desc":
        arr.sort((a, b) => b.name.localeCompare(a.name));
        break;
      default:
        // createdAt_desc
        arr.sort((a, b) => String(b.createdAt || "").localeCompare(String(a.createdAt || "")));
    }
    return arr;
  }, [items, query, sortKey]);

  return (
    <div className="main small-top">
      {/* HEADER */}
      <div className="ch-list-header">
        <div>
          <h1 className="section-title" style={{ marginBottom: 6 }}>Campionati</h1>
          <p className="subtitle" style={{ marginTop: 6 }}>
            Crea un campionato con copertina, poi gestisci le <b>Formule</b> (sottocategorie).  
            Ogni card mostra chiaramente <b>quante formule</b> sono associate.
          </p>
        </div>
        <div className="ch-toolbar">
          <div className="ch-search">
            <span className="ch-search-ic">üîé</span>
            <input
              className="ch-input"
              placeholder="Cerca per nome o formula‚Ä¶"
              value={query}
              onChange={(e) => setQuery(e.target.value)}
            />
          </div>
          <div className="ch-sort">
            <label>Ordina:</label>
            <select className="input" value={sortKey} onChange={(e) => setSortKey(e.target.value)}>
              <option value="createdAt_desc">Pi√π recenti</option>
              <option value="name_asc">Nome A‚ÜíZ</option>
              <option value="name_desc">Nome Z‚ÜíA</option>
            </select>
          </div>
        </div>
      </div>

      {/* CREA CAMPIONATO */}
      <form className="pilot-form" onSubmit={createChamp}>
        <div className="form-row" style={{ gridTemplateColumns: "1.3fr 1fr auto" }}>
          <div className="form-col">
            <label>Nome del Campionato *</label>
            <input
              type="text"
              className="input"
              placeholder="Es. Formula GP Italia"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </div>
          <div className="form-col">
            <label>Foto Campionato (opzionale)</label>
            <div
              className={`dz ${photoPreview ? "has-preview" : ""}`}
              onDragOver={(e) => e.preventDefault()}
              onDrop={onDrop}
              onClick={() => document.getElementById("chFile").click()}
            >
              {!photoPreview ? (
                <div className="dz-inner">
                  <span className="dz-label">Trascina qui o clicca per caricare</span>
                  <span className="dz-hint">Formato .jpg, .png, .webp</span>
                </div>
              ) : (
                <div className="dz-preview">
                  <div className="champ-thumb">
                    <img src={photoPreview} alt="Preview" />
                  </div>
                  <span className="dz-filename">{photoFile?.name}</span>
                </div>
              )}
              <input
                id="chFile"
                type="file"
                accept="image/*"
                style={{ display: "none" }}
                onChange={(e) => onPickFile(e.target.files?.[0])}
              />
            </div>
          </div>
          <div className="form-col" style={{ display: "flex", alignItems: "end" }}>
            <button className="btn-primary" type="submit" disabled={saving}>
              {saving ? "Salvataggio‚Ä¶" : "Aggiungi Campionato"}
            </button>
          </div>
        </div>
        {error && <div className="error" style={{ marginTop: 10 }}>{error}</div>}
      </form>

      {/* LISTA */}
      <h2 className="section-subtitle" style={{ marginTop: 18 }}>Campionati salvati</h2>

      {loadingList ? (
        <div className="cr-empty"><div className="cr-empty-emoji">‚è≥</div><div className="cr-empty-title">Carico‚Ä¶</div></div>
      ) : filtered.length === 0 ? (
        <div className="cr-empty">
          <div className="cr-empty-emoji">üèÅ</div>
          <div className="cr-empty-title">Nessun campionato</div>
          <p className="muted">Prova a rimuovere i filtri o a crearne uno nuovo.</p>
        </div>
      ) : (
        <div className="cr-grid">
          {filtered.map((ch) => {
            const count = ch.formulas?.length || 0;
            return (
              <div className="champ-card" key={ch.id}>
                {/* COVER */}
                <div className="champ-cover">
                  {ch.photo ? (
                    <img src={`${API_BASE}${ch.photo}`} alt={ch.name} />
                  ) : (
                    <div className="champ-cover-empty">Nessuna immagine</div>
                  )}
                  <div className="champ-cover-badge">{ch.name}</div>
                  <div className="champ-counter" title="Numero di formule">{count}</div>
                </div>

                {/* BODY */}
                <div className="champ-body">
                  <div className="champ-head">
                    <div>
                      <div className="champ-title">{ch.name}</div>
                      <div className="champ-sub">
                        Creato il {ch.createdAt ? new Date(ch.createdAt).toLocaleDateString() : "‚Äî"}
                      </div>
                    </div>
                    <div className="champ-actions">
                      <span className="pill pill-number">{count} formule</span>
                      <button
                        type="button"
                        className="btn-ghost"
                        onClick={() => setExpanded((p) => ({ ...p, [ch.id]: !p[ch.id] }))}
                        aria-expanded={!!expanded[ch.id]}
                        title="Gestisci formule"
                      >
                        {expanded[ch.id] ? "Nascondi" : "Gestisci"}
                      </button>
                      <button
                        type="button"
                        className="btn-danger"
                        onClick={() => deleteChamp(ch.id)}
                        title="Elimina campionato"
                      >
                        Elimina
                      </button>
                    </div>
                  </div>

                  {/* GRIGLIA CHIP ‚Äì VISTA COMPATTA */}
                  <div className="formula-grid">
                    {count ? (
                      ch.formulas.map((f, i) => (
                        <div className="formula-chip" key={f.id} title={f.label}>
                          <span className="formula-index">{i + 1}</span>
                          <span className="formula-label">{f.label}</span>
                        </div>
                      ))
                    ) : (
                      <span className="pill ghost">Nessuna formula</span>
                    )}
                  </div>

                  {/* PANNELLO GESTIONE */}
                  {expanded[ch.id] && (
                    <div className="champ-manage">
                      <div className="champ-formulas-manage">
                        {count ? (
                          ch.formulas.map((f, i) => (
                            <div className="champ-formula-row" key={f.id}>
                              <div className="formula-item">
                                <span className="formula-index big">{i + 1}</span>
                                <span className="formula-text">{f.label}</span>
                              </div>
                              <button
                                type="button"
                                className="btn-danger"
                                onClick={() => deleteFormula(ch.id, f.id)}
                                title="Elimina formula"
                              >
                                Elimina
                              </button>
                            </div>
                          ))
                        ) : (
                          <div className="muted">Nessuna formula ancora.</div>
                        )}
                      </div>

                      <div className="form-row" style={{ gridTemplateColumns: "1fr auto" }}>
                        <div className="form-col">
                          <label>Nuova formula</label>
                          <input
                            className="input"
                            placeholder="Es. Formula 2, Junior, Endurance‚Ä¶"
                            value={newFormula[ch.id] || ""}
                            onChange={(e) =>
                              setNewFormula((p) => ({ ...p, [ch.id]: e.target.value }))
                            }
                            onKeyDown={(e) => {
                              if (e.key === "Enter") addFormula(ch.id);
                            }}
                          />
                        </div>
                        <div className="form-col" style={{ display: "flex", alignItems: "end", justifyContent: "flex-end" }}>
                          <button
                            type="button"
                            className="btn-primary"
                            onClick={() => addFormula(ch.id)}
                            disabled={
                              savingFormulaId === ch.id ||
                              !(newFormula[ch.id] || "").trim()
                            }
                          >
                            {savingFormulaId === ch.id ? "Aggiungo‚Ä¶" : "Aggiungi Formula"}
                          </button>
                        </div>
                      </div>
                    </div>
                  )}
                </div>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
}
