    process.env.REACT_APP_API_BASE || `http://${window.location.hostname}:5000`;

  // === STATE ===
  const [list, setList] = useState([]);
  const [loading, setLoading] = useState(true);

  // Create form
  const [name, setName] = useState("");
  const [photoFile, setPhotoFile] = useState(null);
  const [photoPreview, setPhotoPreview] = useState(null);
  const [savingChamp, setSavingChamp] = useState(false);
  const [formError, setFormError] = useState("");

  // Search
  const [query, setQuery] = useState("");

  // Formule per-card
  const [newFormulaByChamp, setNewFormulaByChamp] = useState({});
  const [savingFormulaId, setSavingFormulaId] = useState(null);

  // === HELPERS ===
  const fetchAll = async () => {
    setLoading(true);
    try {
      const r = await fetch(`${API_BASE}/api/championships`);
      const data = await r.json();
      const normalized = (data || []).map((c) => ({
        ...c,
        formulas: Array.isArray(c.formulas) ? c.formulas : [],
      }));
      setList(normalized);
    } catch {
      setList([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAll();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [API_BASE]);

  // === CREATE CHAMPIONSHIP ===
  const onPickFile = (file) => {
    if (!file) return;
    setPhotoFile(file);
    const rd = new FileReader();
    rd.onloadend = () => setPhotoPreview(rd.result);
    rd.readAsDataURL(file);
  };
  const onDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    const f = e.dataTransfer.files?.[0];
    if (f) onPickFile(f);
  };

  const handleCreate = async (e) => {
    e.preventDefault();
    setFormError("");
    if (!name.trim()) {
      setFormError("Inserisci il nome del campionato");
      return;
    }
    setSavingChamp(true);
    try {
      const fd = new FormData();
      fd.append("name", name.trim());
      if (photoFile) fd.append("photo", photoFile);

      const r = await fetch(`${API_BASE}/api/championships`, {
        method: "POST",
        body: fd,
      });
      if (!r.ok) throw new Error("HTTP " + r.status);
      const data = await r.json();
      const normalized = (data || []).map((c) => ({
        ...c,
        formulas: Array.isArray(c.formulas) ? c.formulas : [],
      }));
      setList(normalized);
      setName("");
      setPhotoFile(null);
      setPhotoPreview(null);
    } catch (err) {
      console.error(err);
      setFormError("Errore durante il salvataggio del campionato.");
    } finally {
      setSavingChamp(false);
    }
  };

  // === DELETE CHAMPIONSHIP ===
  const handleDeleteChamp = async (id) => {
    if (!window.confirm("Eliminare il campionato? L‚Äôoperazione non √® reversibile.")) return;
    try {
      const r = await fetch(`${API_BASE}/api/championships/${encodeURIComponent(id)}`, {
        method: "DELETE",
      });
      if (!r.ok) throw new Error("HTTP " + r.status);
      const data = await r.json();
      const normalized = (data || []).map((c) => ({
        ...c,
        formulas: Array.isArray(c.formulas) ? c.formulas : [],
      }));
      setList(normalized);
    } catch (err) {
      console.error(err);
      alert("Errore durante l'eliminazione del campionato.");
    }
  };

  // === FORMULE: ADD/DELETE ===
  const handleAddFormula = async (champId) => {
    const label = (newFormulaByChamp[champId] || "").trim();
    if (!label) return;
    setSavingFormulaId(champId);
    try {
      const r = await fetch(
        `${API_BASE}/api/championships/${encodeURIComponent(champId)}/formulas`,
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ label }),
        }
      );
      if (!r.ok) throw new Error("HTTP " + r.status);
      const updated = await r.json();
      setList((prev) =>
        prev.map((c) => (c.id === updated.id ? { ...updated, formulas: updated.formulas || [] } : c))
      );
      setNewFormulaByChamp((p) => ({ ...p, [champId]: "" }));
    } catch (err) {
      console.error(err);
      alert("Errore durante l'aggiunta della formula.");
    } finally {
      setSavingFormulaId(null);
    }
  };

  const handleDeleteFormula = async (champId, formulaId) => {
    if (!window.confirm("Eliminare questa formula?")) return;
    try {
      const r = await fetch(
        `${API_BASE}/api/championships/${encodeURIComponent(
          champId
        )}/formulas/${encodeURIComponent(formulaId)}`,
        { method: "DELETE" }
      );
      if (!r.ok) throw new Error("HTTP " + r.status);
      const updated = await r.json();
      setList((prev) =>
        prev.map((c) => (c.id === updated.id ? { ...updated, formulas: updated.formulas || [] } : c))
      );
    } catch (err) {
      console.error(err);
      alert("Errore durante l'eliminazione della formula.");
    }
  };

  // === FILTERED VIEW ===
  const filtered = useMemo(() => {
    const q = query.trim().toLowerCase();
    if (!q) return list;
    return list.filter((c) => {
      const hay =
        `${c.name || ""} ${(c.formulas || []).map((f) => f.label).join(" ")}`.toLowerCase();
      return hay.includes(q);
    });
  }, [list, query]);

  return (
    <div className="main small-top">

      {/* 1) CREA NUOVO CAMPIONATO */}
      <section className="ch-block">
        <header className="ch-block__header">
          <h2 className="ch-block__title">Crea nuovo campionato</h2>
          <p className="ch-block__subtitle">Nome e (opzionale) copertina.</p>
        </header>

        <form className="ch-create" onSubmit={handleCreate}>
          <div className="form-col">
            <label>Nome *</label>
            <input
              className="input"
              placeholder="Es. Campionato Kart Junior"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </div>

          <div className="form-col">
            <label>Copertina (opzionale)</label>
            <div
              className={`dz ${photoPreview ? "has-preview" : ""}`}
              onDragOver={(e) => e.preventDefault()}
              onDrop={onDrop}
              onClick={() => document.getElementById("chFile").click()}
            >
              {!photoPreview ? (
                <div className="dz-inner">
                  <span className="dz-label">Trascina qui o clicca per caricare</span>
                  <span className="dz-hint">.jpg .png .webp</span>
                </div>
              ) : (
                <div className="dz-preview">
                  <div className="ch-thumb">
                    <img src={photoPreview} alt="Anteprima" />
                  </div>
                  <span className="dz-filename">{photoFile?.name}</span>
                </div>
              )}
              <input
                id="chFile"
                type="file"
                accept="image/*"
                style={{ display: "none" }}
                onChange={(e) => onPickFile(e.target.files?.[0])}
              />
            </div>
          </div>

          <div className="form-col ch-create__submit">
            <button className="btn-primary" type="submit" disabled={savingChamp}>
              {savingChamp ? "Salvataggio‚Ä¶" : "Crea campionato"}
            </button>
            {formError && <div className="error" style={{ marginTop: 8 }}>{formError}</div>}
          </div>
        </form>
      </section>

      {/* 2) BARRA DI RICERCA */}
      <section className="ch-block">
        <header className="ch-block__header">
          <h2 className="ch-block__title">Cerca campionati</h2>
          <p className="ch-block__subtitle">Filtra per nome o formula.</p>
        </header>

        <div className="ch-searchbar">
          <span className="ch-searchbar__icon" aria-hidden>üîé</span>
          <input
            className="ch-searchbar__input"
            placeholder="Es. Endurance, Junior, Formula 2‚Ä¶"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
          />
          {query && (
            <button className="btn-ghost" type="button" onClick={() => setQuery("")}>
              Pulisci
            </button>
          )}
        </div>
      </section>

      {/* 3) LISTA CAMPIONATI */}
      <section className="ch-block">
        <header className="ch-block__header">
          <h2 className="ch-block__title">Tutti i campionati</h2>
          <p className="ch-block__subtitle">
            Struttura chiara: card ‚Üí copertina ‚Üí info ‚Üí lista formule (con elimina).
          </p>
        </header>

        {loading ? (
          <div className="cr-empty">
            <div className="cr-empty-emoji">‚è≥</div>
            <div className="cr-empty-title">Carico‚Ä¶</div>
          </div>
        ) : filtered.length === 0 ? (
          <div className="cr-empty">
            <div className="cr-empty-emoji">üèÅ</div>
            <div className="cr-empty-title">Nessun campionato</div>
            <p className="muted">Crea un campionato o modifica la ricerca.</p>
          </div>
        ) : (
          <div className="ch-list">
            {filtered.map((ch) => (
              <article className="ch-card" key={ch.id}>
                {/* Cover */}
                <div className="ch-card__cover">
                  {ch.photo ? (
                    <img src={`${API_BASE}${ch.photo}`} alt={ch.name} />
                  ) : (
                    <div className="ch-card__cover--empty">Nessuna immagine</div>
                  )}
                </div>

                {/* Body */}
                <div className="ch-card__body">
                  <div className="ch-card__row">
                    <div className="ch-card__title">
                      <h3>{ch.name}</h3>
                      <small className="muted">
                        Creato il {ch.createdAt ? new Date(ch.createdAt).toLocaleDateString() : "‚Äî"}
                      </small>
                    </div>

                    <div className="ch-card__actions">
                      <span className="pill pill-number" title="Formule totali">
                        {(ch.formulas?.length || 0)} formule
                      </span>
                      <button
                        type="button"
                        className="btn-danger"
                        onClick={() => handleDeleteChamp(ch.id)}
                        title="Elimina campionato"
                      >
                        Elimina campionato
                      </button>
                    </div>
                  </div>

                  {/* Lista formule */}
                  <div className="ch-card__section">
                    <div className="ch-card__section-title">Formule</div>

                    {ch.formulas?.length ? (
                      <ul className="formula-list" aria-label={`Formule di ${ch.name}`}>
                        {ch.formulas.map((f, i) => (
                          <li className="formula-row" key={f.id}>
                            <div className="formula-row__left">
                              <span className="formula-row__index">{i + 1}</span>
                              <span className="formula-row__label">{f.label}</span>
                            </div>
                            <div className="formula-row__right">
                              <button
                                className="btn-danger"
                                type="button"
                                onClick={() => handleDeleteFormula(ch.id, f.id)}
                                title="Elimina formula"
                              >
                                Elimina
                              </button>
                            </div>
                          </li>
                        ))}
                      </ul>
                    ) : (
                      <div className="formula-empty muted">Nessuna formula</div>
                    )}

                    {/* Aggiungi formula */}
                    <div className="formula-add">
                      <input
                        className="input"
                        placeholder="Nuova formula (es. Formula 2, Junior, Endurance)"
                        value={newFormulaByChamp[ch.id] || ""}
                        onChange={(e) =>
                          setNewFormulaByChamp((p) => ({ ...p, [ch.id]: e.target.value }))
                        }
                        onKeyDown={(e) => {
                          if (e.key === "Enter") handleAddFormula(ch.id);
                        }}
                      />
                      <button
                        className="btn-primary"
                        type="button"
                        onClick={() => handleAddFormula(ch.id)}
                        disabled={
                          savingFormulaId === ch.id || !(newFormulaByChamp[ch.id] || "").trim()
                        }
                      >
                        {savingFormulaId === ch.id ? "Aggiungo‚Ä¶" : "Aggiungi"}
                      </button>
                    </div>
                  </div>
                </div>
              </article>
            ))}
          </div>
        )}
      </section>
    </div>
  );
}
