import React from 'react';

/** Campionati statici richiesti */
const CHAMPIONSHIPS = ['iron', 'iron inx', 'spint'];

/** Dropzone semplice (drag&drop o click) */
function Dropzone({ label, file, onFileChange, accept = 'image/*' }) {
  const inputRef = React.useRef(null);
  const [isOver, setIsOver] = React.useState(false);

  const onDrop = (e) => {
    e.preventDefault();
    setIsOver(false);
    const f = e.dataTransfer.files?.[0];
    if (f) onFileChange(f);
  };
  const onClick = () => inputRef.current?.click();

  return (
    <div
      className={`dz ${isOver ? 'over' : ''}`}
      onDragOver={(e) => { e.preventDefault(); setIsOver(true); }}
      onDragLeave={() => setIsOver(false)}
      onDrop={onDrop}
      onClick={onClick}
      role="button"
      tabIndex={0}
    >
      {file ? (
        <div className="dz-preview">
          <img src={URL.createObjectURL(file)} alt="preview" />
          <span className="dz-filename">{file.name}</span>
        </div>
      ) : (
        <div className="dz-inner">
          <span className="dz-label">{label}</span>
          <span className="dz-hint">Trascina qui o clicca</span>
        </div>
      )}
      <input
        ref={inputRef}
        type="file"
        accept={accept}
        style={{ display: 'none' }}
        onChange={(e) => {
          const f = e.target.files?.[0];
          if (f) onFileChange(f);
        }}
      />
    </div>
  );
}

export default function PilotsPage({ apiBase }) {
  const [list, setList] = React.useState([]);
  const [loading, setLoading] = React.useState(true);
  const [error, setError] = React.useState('');

  // form state
  const [name, setName] = React.useState('');
  const [surname, setSurname] = React.useState('');
  const [team, setTeam] = React.useState('');
  const [champSel, setChampSel] = React.useState([]);
  const [photoDriver, setPhotoDriver] = React.useState(null);
  const [photoTeam, setPhotoTeam] = React.useState(null);
  const [submitting, setSubmitting] = React.useState(false);

  const load = React.useCallback(() => {
    setLoading(true);
    setError('');
    fetch(`${apiBase}/api/pilots`)
      .then(r => r.ok ? r.json() : Promise.reject(new Error(`HTTP ${r.status}`)))
      .then(json => setList(json))
      .catch(e => setError(e.message || 'Errore caricamento'))
      .finally(() => setLoading(false));
  }, [apiBase]);

  React.useEffect(() => { load(); }, [load]);

  const toggleChamp = (c) => {
    setChampSel(prev => prev.includes(c) ? prev.filter(x => x !== c) : [...prev, c]);
  };

  const onSubmit = (e) => {
    e.preventDefault();
    if (!name.trim() || !surname.trim() || !team.trim()) {
      alert('Compila Nome, Cognome e Team.');
      return;
    }
    setSubmitting(true);
    const fd = new FormData();
    fd.append('name', name.trim());
    fd.append('surname', surname.trim());
    fd.append('team', team.trim());
    fd.append('championships', JSON.stringify(champSel));
    if (photoDriver) fd.append('photoDriver', photoDriver);
    if (photoTeam) fd.append('photoTeam', photoTeam);

    fetch(`${apiBase}/api/pilots`, { method: 'POST', body: fd })
      .then(r => r.ok ? r.json() : r.json().catch(() => ({})).then(j => Promise.reject(new Error(j.error || `HTTP ${r.status}`))))
      .then(() => {
        setName(''); setSurname(''); setTeam('');
        setChampSel([]); setPhotoDriver(null); setPhotoTeam(null);
        load();
      })
      .catch(e => alert(`Errore salvataggio: ${e.message}`))
      .finally(() => setSubmitting(false));
  };

  const onDelete = (id) => {
    if (!window.confirm('Eliminare il pilota?')) return;
    fetch(`${apiBase}/api/pilots/${id}`, { method: 'DELETE' })
      .then(r => r.ok ? r.json() : Promise.reject(new Error(`HTTP ${r.status}`)))
      .then(() => setList(prev => prev.filter(p => p.id !== id)))
      .catch(e => alert(`Errore eliminazione: ${e.message}`));
  };

  return (
    <div className="pilots-page">
      {/* Stili locali per il layout della card come nello sketch */}
      <style>{`
        .pilot-card.sketch {
          background: linear-gradient(180deg, rgba(255,255,255,0.06), rgba(255,255,255,0.02));
          border: 1px solid var(--line);
          border-radius: var(--radius);
          padding: 18px;
          box-shadow: var(--shadow);
          display: grid;
          grid-template-columns: 180px 1fr;
          gap: 18px;
          align-items: start;
        }
        .pilot-left {
          display: grid;
          grid-template-rows: 180px 120px;
          gap: 12px;
        }
        .pilot-photoBox {
          background: var(--glass);
          border: 1px solid var(--line);
          border-radius: 14px;
          overflow: hidden;
          display: grid;
          place-items: center;
        }
        .pilot-photoBox img {
          width: 100%; height: 100%; object-fit: cover;
        }
        .pilot-right {
          display: grid;
          grid-template-rows: auto auto 1fr auto;
          gap: 8px;
          min-height: 180px;
        }
        .pilot-title {
          font-size: 1.8rem;
          font-weight: 900;
          line-height: 1.1;
          letter-spacing: .4px;
        }
        .pilot-teamName {
          color: var(--muted);
          font-size: 1.1rem;
          margin-bottom: 6px;
        }
        .pilot-chipsCol {
          display: grid;
          gap: 10px;
          align-content: start;
          padding-top: 6px;
        }
        .chip {
          border: 1px solid var(--line);
          background: var(--glass);
          padding: 8px 14px;
          border-radius: 999px;
          width: max-content;
          font-size: 1rem;
        }
        .pilot-actionsRow {
          display: grid;
          grid-template-columns: 1fr auto;
          align-items: end;
          margin-top: 6px;
        }
        .btn-danger {
          background: transparent;
          color: var(--down);
          border: 1px solid rgba(255,107,107,0.5);
          padding: 10px 16px;
          border-radius: 12px;
          cursor: pointer;
          transition: .2s;
        }
        .btn-danger:hover { background: rgba(255,107,107,0.08); }
        .pilots-list { display: grid; gap: 16px; }
        .section-title{
          color: var(--brand); font-weight: 900; font-size: 1.8rem; letter-spacing:.5px; margin-bottom: 14px;
        }
        .section-subtitle{ font-size: 1.2rem; margin: 24px 0 10px; color: var(--fg); }
        .pilot-form{
          background: linear-gradient(180deg, rgba(255,255,255,0.06), rgba(255,255,255,0.02));
          border: 1px solid var(--line); border-radius: var(--radius); padding: 18px; box-shadow: var(--shadow);
          margin-bottom: 20px; text-align: left;
        }
        .form-row{ display:grid; grid-template-columns: repeat(3, minmax(0,1fr)); gap:14px; margin-bottom:12px; }
        .form-col label{ display:block; font-size:.95rem; margin-bottom:6px; color:var(--fg); font-weight:600; }
        .input{ width:100%; background:var(--glass); border:1px solid var(--line); border-radius:10px; padding:10px 12px; color:var(--fg); outline:none; }
        .input:focus{ border-color: rgba(192,255,3,0.55); }
        .chips{ display:flex; gap:8px; flex-wrap:wrap; }
        .chip.toggle{ cursor:pointer; }
        .chip.toggle.active{ background: rgba(192,255,3,0.15); border-color: rgba(192,255,3,0.55); }
        .form-actions{ margin-top:6px; display:flex; justify-content:flex-end; }
        .btn-primary{ background:var(--brand); color:#0a0a0a; border:none; padding:10px 16px; border-radius:10px; font-weight:800; cursor:pointer; }
        .btn-primary:disabled{ opacity:.6; cursor:not-allowed; }
        .dz{ border:1px dashed var(--line); background:var(--glass); border-radius:12px; padding:14px; text-align:center; min-height:110px; display:grid; place-items:center; cursor:pointer; transition:.2s; }
        .dz.over{ border-color: rgba(192,255,3,0.55); background: rgba(192,255,3,0.08); }
        .dz-inner .dz-label{ display:block; font-weight:700; }
        .dz-inner .dz-hint{ display:block; color:var(--muted); font-size:.9rem; margin-top:4px; }
        .dz-preview{ display:flex; flex-direction:column; align-items:center; gap:6px; }
        .dz-preview img{ max-height:70px; max-width:100%; border-radius:8px; object-fit:cover; }
        .dz-filename{ font-size:.85rem; color:var(--muted); max-width:100%; overflow:hidden; text-overflow:ellipsis; }
        .muted{ color: var(--muted); font-size: .9rem; }
        .error { color: var(--down); background: rgba(255,107,107,0.1); border: 1px solid rgba(255,107,107,0.3); padding:10px; border-radius:12px; }

        @media (max-width: 960px){
          .form-row{ grid-template-columns: 1fr; }
          .pilot-card.sketch{ grid-template-columns: 1fr; }
          .pilot-left{ grid-template-rows: 200px 140px; }
          .pilot-actionsRow{ grid-template-columns: 1fr; justify-items: end; }
        }
      `}</style>

      <div className="bg-grid" aria-hidden="true" />
      <div className="bg-glow bg-glow-1" aria-hidden="true" />
      <div className="bg-glow bg-glow-2" aria-hidden="true" />

      <main className="main small-top">
        <h2 className="section-title">Gestione Piloti</h2>

        {/* ===== FORM CREAZIONE ===== */}
        <form className="pilot-form" onSubmit={onSubmit}>
          <div className="form-row">
            <div className="form-col">
              <label>Nome</label>
              <input className="input" value={name} onChange={(e) => setName(e.target.value)} placeholder="Es. Mario" required />
            </div>
            <div className="form-col">
              <label>Cognome</label>
              <input className="input" value={surname} onChange={(e) => setSurname(e.target.value)} placeholder="Es. Rossi" required />
            </div>
            <div className="form-col">
              <label>Team</label>
              <input className="input" value={team} onChange={(e) => setTeam(e.target.value)} placeholder="Es. Nexter Racing" required />
            </div>
          </div>

          <div className="form-row">
            <div className="form-col">
              <label>Campionati</label>
              <div className="chips">
                {CHAMPIONSHIPS.map(c => (
                  <span
                    key={c}
                    className={`chip toggle ${champSel.includes(c) ? 'active' : ''}`}
                    onClick={() => toggleChamp(c)}
                    role="button"
                    tabIndex={0}
                  >
                    {c}
                  </span>
                ))}
              </div>
              <small className="muted">Seleziona uno o pi√π campionati</small>
            </div>
          </div>

          <div className="form-row">
            <div className="form-col">
              <label>Foto Pilota</label>
              <Dropzone label="Foto pilota" file={photoDriver} onFileChange={setPhotoDriver} accept="image/*" />
            </div>
            <div className="form-col">
              <label>Foto Team</label>
              <Dropzone label="Logo/Foto team" file={photoTeam} onFileChange={setPhotoTeam} accept="image/*" />
            </div>
            <div className="form-col" />
          </div>

          <div className="form-actions">
            <button className="btn-primary" type="submit" disabled={submitting}>
              {submitting ? 'Salvataggio...' : 'Crea Pilota'}
            </button>
          </div>
        </form>

        {/* ===== LISTA PILOTI ===== */}
        <h3 className="section-subtitle">Piloti esistenti</h3>

        {loading && <p className="muted">Caricamento...</p>}
        {error && <p className="error">{error}</p>}

        {!loading && !error && (
          list.length === 0 ? (
            <p className="muted">Nessun pilota presente.</p>
          ) : (
            <div className="pilots-list">
              {list.map(p => (
                <div key={p.id} className="pilot-card sketch">
                  {/* Colonna SINISTRA: foto profilo (in alto) + foto team (in basso) */}
                  <div className="pilot-left">
                    <div className="pilot-photoBox">
                      {p.photoDriverUrl
                        ? <img src={`${apiBase}${p.photoDriverUrl}`} alt={`${p.name} ${p.surname}`} />
                        : <div className="photo-placeholder">foto profilo</div>}
                    </div>
                    <div className="pilot-photoBox">
                      {p.photoTeamUrl
                        ? <img src={`${apiBase}${p.photoTeamUrl}`} alt={`${p.team}`} />
                        : <div className="photo-placeholder">foto team</div>}
                    </div>
                  </div>

                  {/* Colonna DESTRA: nome/cognome, team, campionati, elimina in basso a dx */}
                  <div className="pilot-right">
                    <div className="pilot-title">{p.name} {p.surname}</div>
                    <div className="pilot-teamName">{p.team}</div>

                    <div className="pilot-chipsCol">
                      {(p.championships || []).map(c => (
                        <span key={c} className="chip">{c}</span>
                      ))}
                    </div>

                    <div className="pilot-actionsRow">
                      <div /> {/* spacer */}
                      <button className="btn-danger" onClick={() => onDelete(p.id)}>Elimina</button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )
        )}
      </main>
    </div>
  );
}
