import React, { useState, useEffect, useRef, useCallback } from 'react';
import './Race.css';

const API_BASE = process.env.REACT_APP_API_BASE || `http://${window.location.hostname}:5000`;
const WS_URL = process.env.REACT_APP_WS_URL || `ws://${window.location.hostname}:5001`;

// Utility: calcola distanza Haversine (m)
function haversine(lat1, lon1, lat2, lon2) {
  const R = 6371000;
  const toRad = d => d * Math.PI / 180;
  const dLat = toRad(lat2 - lat1);
  const dLon = toRad(lon2 - lon1);
  const a = Math.sin(dLat / 2) ** 2 + 
            Math.cos(toRad(lat1)) * Math.cos(toRad(lat2)) * Math.sin(dLon / 2) ** 2;
  return R * 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
}

// Trova il settore più vicino
function findClosestSector(lat, lon, sectors) {
  let minDist = Infinity;
  let closestIdx = 0;
  sectors.forEach((s, i) => {
    const d = haversine(lat, lon, s.lat, s.lon);
    if (d < minDist) {
      minDist = d;
      closestIdx = i;
    }
  });
  return closestIdx;
}

export default function RaceLive({ raceConfig, onStopRace }) {
  const { circuitData, totalLaps, assignments, pilots } = raceConfig;

  const [driversState, setDriversState] = useState({});
  const [leaderboard, setLeaderboard] = useState([]);
  const [activeBattles, setActiveBattles] = useState([]);

  const canvasRef = useRef(null);
  const wsRef = useRef(null);
  const animFrameRef = useRef(null);
  const zoomRef = useRef(1);
  const panRef = useRef({ x: 0, y: 0 });
  const isDraggingRef = useRef(false);
  const lastMouseRef = useRef({ x: 0, y: 0 });

  // ========== WebSocket GPS ==========
  useEffect(() => {
    const ws = new WebSocket(WS_URL);
    wsRef.current = ws;

    ws.onopen = () => console.log('[RaceLive] WebSocket connesso');

    ws.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data);

        // Formato: il backend invia { mac, lat, lon, speedKmh, ... }
        if (data.mac && data.lat && data.lon) {
          const mac = data.mac;

          // Ignora device non assegnati
          if (!assignments[mac]) return;

          const gps = {
            mac,
            lat: data.lat,
            lon: data.lon,
            speed: data.speedKmh || 0,
            ts: data.ts
          };

          handleGPSUpdate(gps);
        }
      } catch (e) {
        console.error('[WS] Parse error:', e);
      }
    };

    ws.onerror = (err) => console.error('[WS] Errore:', err);
    ws.onclose = () => console.log('[WS] Chiuso');

    return () => {
      if (ws.readyState === WebSocket.OPEN) ws.close();
    };
  }, [assignments]);

  // ========== Generazione colore team stile F1 ==========
  const getTeamColor = useCallback((pilot, allPilots) => {
    if (pilot.color) return pilot.color;

    // Trova tutti i piloti dello stesso team
    const teamPilots = allPilots.filter(p => p.team === pilot.team);
    const pilotIndex = teamPilots.findIndex(p => p.id === pilot.id);

    // Genera un colore base per il team (basato sul nome team)
    const teamHash = pilot.team.split('').reduce((acc, char) => acc + char.charCodeAt(0), 0);
    const baseHue = (teamHash * 137.508) % 360; // Golden angle per distribuzione uniforme

    // Se c'è più di un pilota nel team, varia leggermente luminosità e saturazione
    if (teamPilots.length > 1) {
      const lightness = 50 + (pilotIndex * 10); // Varia luminosità: 50%, 60%, 70%...
      const saturation = 75 - (pilotIndex * 5); // Varia saturazione: 75%, 70%, 65%...
      return `hsl(${baseHue}, ${saturation}%, ${lightness}%)`;
    }

    // Colore base team
    return `hsl(${baseHue}, 75%, 55%)`;
  }, []);

  // ========== Gestione GPS Update ==========
  const handleGPSUpdate = useCallback((gps) => {
    if (!circuitData?.sectors) return;

    const { mac, lat, lon, speed } = gps;
    const sectorIdx = findClosestSector(lat, lon, circuitData.sectors);
    const totalSectors = circuitData.sectors.length;

    // Trova pilota assegnato
    const pilotId = assignments[mac];
    const pilot = pilots.find(p => p.id === pilotId);
    if (!pilot) return;

    setDriversState(prev => {
      const existing = prev[mac] || {
        mac,
        name: `${pilot.name} ${pilot.surname}`,
        team: pilot.team,
        color: getTeamColor(pilot, pilots),
        photoTeamUrl: pilot.photoTeamUrl,
        photoDriverUrl: pilot.photoDriverUrl,
        lat,
        lon,
        speed: 0,
        sectorIdx,
        lapCount: 0,
        lastLapTime: null,
        bestLapTime: null,
        lastSectorIdx: sectorIdx,
        lapStartTime: Date.now(),
        trail: [],
      };

      // Rilevamento completamento giro
      let newLapCount = existing.lapCount;
      let newLapTime = existing.lastLapTime;
      let newBestLap = existing.bestLapTime;

      if (existing.lastSectorIdx > totalSectors - 10 && sectorIdx < 10) {
        const lapDuration = (Date.now() - existing.lapStartTime) / 1000;
        if (lapDuration > 5) {
          newLapCount = existing.lapCount + 1;
          newLapTime = lapDuration;
          if (!newBestLap || lapDuration < newBestLap) {
            newBestLap = lapDuration;
          }
          console.log(`[LAP] ${existing.name} completato giro ${newLapCount} in ${lapDuration.toFixed(2)}s`);
        }
      }

      const newTrail = [...existing.trail, { lat, lon }].slice(-20);

      return {
        ...prev,
        [mac]: {
          ...existing,
          lat,
          lon,
          speed,
          sectorIdx,
          lastSectorIdx: sectorIdx,
          lapCount: newLapCount,
          lastLapTime: newLapTime,
          bestLapTime: newBestLap,
          lapStartTime: newLapCount > existing.lapCount ? Date.now() : existing.lapStartTime,
          trail: newTrail,
          updatedAt: Date.now(),
        }
      };
    });
  }, [circuitData, assignments, pilots, getTeamColor]);

  // ========== Aggiorna classifica e battaglie ==========
  useEffect(() => {
    const sorted = Object.values(driversState).sort((a, b) => {
      if (a.lapCount !== b.lapCount) return b.lapCount - a.lapCount;
      return b.sectorIdx - a.sectorIdx;
    });
    setLeaderboard(sorted);

    // Battaglie
    const battles = [];
    for (let i = 0; i < sorted.length - 1; i++) {
      const driver1 = sorted[i];
      const driver2 = sorted[i + 1];

      if (driver1.lapCount === driver2.lapCount) {
        const dist = haversine(driver1.lat, driver1.lon, driver2.lat, driver2.lon);
        if (dist < 20) {
          battles.push({
            leader: driver1,
            chaser: driver2,
            distance: dist,
            position: i + 1
          });
        }
      }
    }
    setActiveBattles(battles);
  }, [driversState]);

  // ========== Rendering Canvas ==========
  useEffect(() => {
    if (!circuitData || !canvasRef.current) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const dpr = window.devicePixelRatio || 1;

    const resize = () => {
      const rect = canvas.parentElement.getBoundingClientRect();
      canvas.width = rect.width * dpr;
      canvas.height = rect.height * dpr;
      canvas.style.width = `${rect.width}px`;
      canvas.style.height = `${rect.height}px`;
      ctx.scale(dpr, dpr);
    };
    resize();
    window.addEventListener('resize', resize);

    // Gestione zoom con rotella
    const handleWheel = (e) => {
      e.preventDefault();
      const delta = e.deltaY > 0 ? 0.9 : 1.1;
      zoomRef.current = Math.max(0.5, Math.min(5, zoomRef.current * delta));
    };
    canvas.addEventListener('wheel', handleWheel, { passive: false });

    // Gestione panning con mouse
    const handleMouseDown = (e) => {
      isDraggingRef.current = true;
      lastMouseRef.current = { x: e.clientX, y: e.clientY };
      canvas.style.cursor = 'grabbing';
    };

    const handleMouseMove = (e) => {
      if (!isDraggingRef.current) return;

      const dx = e.clientX - lastMouseRef.current.x;
      const dy = e.clientY - lastMouseRef.current.y;

      panRef.current.x += dx;
      panRef.current.y += dy;

      lastMouseRef.current = { x: e.clientX, y: e.clientY };
    };

    const handleMouseUp = () => {
      isDraggingRef.current = false;
      canvas.style.cursor = 'grab';
    };

    canvas.addEventListener('mousedown', handleMouseDown);
    canvas.addEventListener('mousemove', handleMouseMove);
    canvas.addEventListener('mouseup', handleMouseUp);
    canvas.addEventListener('mouseleave', handleMouseUp);
    canvas.style.cursor = 'grab';

    // Calcola bounds pista
    const lats = circuitData.sectors.map(s => s.lat);
    const lons = circuitData.sectors.map(s => s.lon);
    const minLat = Math.min(...lats);
    const maxLat = Math.max(...lats);
    const minLon = Math.min(...lons);
    const maxLon = Math.max(...lons);

    // Centro del tracciato
    const centerLat = (minLat + maxLat) / 2;
    const centerLon = (minLon + maxLon) / 2;

    const padding = 60;

    // Proiezione ortografica perfettamente piatta (vista dall'alto)
    const project = (lat, lon) => {
      const rect = canvas.getBoundingClientRect();
      const w = rect.width - padding * 2;
      const h = rect.height - padding * 2;

      // Converte lat/lon in metri dal centro usando proiezione equirettangolare corretta
      const R = 6371000; // raggio terra in metri
      const latRad = lat * Math.PI / 180;
      const centerLatRad = centerLat * Math.PI / 180;

      // Distanza in metri dal centro
      const dx = (lon - centerLon) * Math.PI / 180 * R * Math.cos(centerLatRad);
      const dy = (lat - centerLat) * Math.PI / 180 * R;

      // Trova range massimo per scalare uniformemente
      const allDx = lons.map(l => (l - centerLon) * Math.PI / 180 * R * Math.cos(centerLatRad));
      const allDy = lats.map(l => (l - centerLat) * Math.PI / 180 * R);
      const maxDx = Math.max(...allDx.map(Math.abs));
      const maxDy = Math.max(...allDy.map(Math.abs));
      const maxRange = Math.max(maxDx, maxDy) * 1.1; // 10% margin

      // Scala mantenendo aspect ratio con zoom
      const scale = Math.min(w, h) / (maxRange * 2) * zoomRef.current;
      const x = rect.width / 2 + dx * scale + panRef.current.x;
      const y = rect.height / 2 - dy * scale + panRef.current.y; // inverte Y (canvas ha origine in alto)

      return { x, y };
    };

    const draw = () => {
      const rect = canvas.getBoundingClientRect();
      ctx.clearRect(0, 0, rect.width, rect.height);

      // Sfondo pulito
      ctx.fillStyle = '#0f1419';
      ctx.fillRect(0, 0, rect.width, rect.height);

      // Tracciato - stile F1 minimale
      ctx.strokeStyle = 'rgba(60, 65, 75, 0.8)';
      ctx.lineWidth = 20;
      ctx.lineCap = 'round';
      ctx.lineJoin = 'round';
      ctx.beginPath();
      circuitData.sectors.forEach((s, i) => {
        const p = project(s.lat, s.lon);
        i === 0 ? ctx.moveTo(p.x, p.y) : ctx.lineTo(p.x, p.y);
      });
      ctx.closePath();
      ctx.stroke();

      // Bordo esterno tracciato
      ctx.strokeStyle = 'rgba(255, 255, 255, 0.15)';
      ctx.lineWidth = 22;
      ctx.beginPath();
      circuitData.sectors.forEach((s, i) => {
        const p = project(s.lat, s.lon);
        i === 0 ? ctx.moveTo(p.x, p.y) : ctx.lineTo(p.x, p.y);
      });
      ctx.closePath();
      ctx.stroke();

      // Bordo interno tracciato
      ctx.strokeStyle = 'rgba(255, 255, 255, 0.1)';
      ctx.lineWidth = 18;
      ctx.beginPath();
      circuitData.sectors.forEach((s, i) => {
        const p = project(s.lat, s.lon);
        i === 0 ? ctx.moveTo(p.x, p.y) : ctx.lineTo(p.x, p.y);
      });
      ctx.closePath();
      ctx.stroke();

      // Finish line - stile F1 TV professionale
      if (circuitData.sectors.length > 1) {
        const s0 = project(circuitData.sectors[0].lat, circuitData.sectors[0].lon);
        const s1 = project(circuitData.sectors[1].lat, circuitData.sectors[1].lon);
        const angle = Math.atan2(s1.y - s0.y, s1.x - s0.x);

        ctx.save();
        ctx.translate(s0.x, s0.y);
        ctx.rotate(angle);

        // Linea finish pulita e visibile - stile F1 TV (largo quanto la pista)
        const trackWidth = 20; // larghezza pista
        ctx.strokeStyle = '#fff';
        ctx.lineWidth = 4;
        ctx.beginPath();
        ctx.moveTo(0, -trackWidth/2);
        ctx.lineTo(0, trackWidth/2);
        ctx.stroke();

        // Secondo accento rosso per traguardo (come nelle grafiche F1)
        ctx.strokeStyle = '#e10600';
        ctx.lineWidth = 2;
        ctx.beginPath();
        ctx.moveTo(-2, -trackWidth/2);
        ctx.lineTo(-2, trackWidth/2);
        ctx.stroke();

        ctx.restore();
      }

      // Piloti
      const sortedDrivers = Object.values(driversState).sort((a, b) => {
        if (a.lapCount !== b.lapCount) return a.lapCount - b.lapCount;
        return a.sectorIdx - b.sectorIdx;
      });

      sortedDrivers.forEach((driver, idx) => {
        const pos = project(driver.lat, driver.lon);

        // Trail - più visibile e pulito
        if (driver.trail && driver.trail.length > 1) {
          ctx.strokeStyle = driver.color;
          ctx.lineWidth = 3.5;
          ctx.lineCap = 'round';
          ctx.lineJoin = 'round';
          ctx.globalAlpha = 0.65;
          ctx.beginPath();
          driver.trail.forEach((t, ti) => {
            const tp = project(t.lat, t.lon);
            ti === 0 ? ctx.moveTo(tp.x, tp.y) : ctx.lineTo(tp.x, tp.y);
          });
          ctx.stroke();
          ctx.globalAlpha = 1;
        }

        // Driver marker - più visibile e professionale
        const isLeader = idx === sortedDrivers.length - 1;
        const radius = isLeader ? 8 : 7;

        // Ombra leggera per profondità
        ctx.shadowColor = 'rgba(0, 0, 0, 0.4)';
        ctx.shadowBlur = 4;
        ctx.shadowOffsetX = 0;
        ctx.shadowOffsetY = 1;

        // Marker colorato
        ctx.fillStyle = driver.color;
        ctx.beginPath();
        ctx.arc(pos.x, pos.y, radius, 0, Math.PI * 2);
        ctx.fill();

        ctx.shadowBlur = 0;
        ctx.shadowOffsetX = 0;
        ctx.shadowOffsetY = 0;

        // Bordo bianco per contrasto
        ctx.strokeStyle = '#fff';
        ctx.lineWidth = 2;
        ctx.stroke();

        // Nome minimal - solo cognome
        const nameParts = driver.name.split(' ');
        const lastName = nameParts[nameParts.length - 1];
        ctx.fillStyle = '#fff';
        ctx.font = 'bold 10px Roboto, sans-serif';
        ctx.textAlign = 'center';
        ctx.shadowColor = 'rgba(0, 0, 0, 0.9)';
        ctx.shadowBlur = 3;
        ctx.fillText(lastName.toUpperCase(), pos.x, pos.y - 12);
        ctx.shadowBlur = 0;
      });

      animFrameRef.current = requestAnimationFrame(draw);
    };

    draw();

    return () => {
      window.removeEventListener('resize', resize);
      canvas.removeEventListener('wheel', handleWheel);
      canvas.removeEventListener('mousedown', handleMouseDown);
      canvas.removeEventListener('mousemove', handleMouseMove);
      canvas.removeEventListener('mouseup', handleMouseUp);
      canvas.removeEventListener('mouseleave', handleMouseUp);
      if (animFrameRef.current) cancelAnimationFrame(animFrameRef.current);
    };
  }, [circuitData, driversState]);

  // Formatta tempo
  const formatLapTime = (seconds) => {
    if (!seconds) return '--:--.---';
    const mins = Math.floor(seconds / 60);
    const secs = (seconds % 60).toFixed(3);
    return `${mins}:${secs.padStart(6, '0')}`;
  };

  return (
    <div className="race-page">
      <header className="race-header">
        <div className="race-header-left">
          <div className="race-logo">RACESENSE</div>
          <div className="race-title">LIVE RACE</div>
        </div>
        <div className="race-header-right">
          <div className="circuit-badge">
            <span className="circuit-icon"></span>
            <span className="circuit-name">{circuitData.name}</span>
          </div>
          <div className="laps-badge">
            <span className="laps-icon"></span>
            <span className="laps-text">{totalLaps} LAPS</span>
          </div>
        </div>
      </header>

      <div className="race-live">
        <div className="race-main">
          <div className="track-container">
            <canvas ref={canvasRef} className="track-canvas" />

            <div className="track-overlay">
              <div className="live-indicator">
                <span className="live-dot"></span>
                LIVE
              </div>

              {circuitData && (
                <div className="track-info">
                  <div className="track-stat">
                    <span className="stat-icon">LENGTH</span>
                    <span className="stat-text">{circuitData.stats?.lengthMeters?.toFixed(0)}m</span>
                  </div>
                </div>
              )}
            </div>
          </div>

          <aside className="leaderboard">
            <div className="leaderboard-header">
              <h3>CLASSIFICATION</h3>
              <div className="leaderboard-subtitle">
                {leaderboard.length} Drivers
              </div>
            </div>

            <div className="leaderboard-list">
              {leaderboard.length === 0 ? (
                <div className="no-drivers">
                  <p>⏳ In attesa di dati GPS...</p>
                </div>
              ) : (
                leaderboard.map((driver, pos) => {
                  const battle = activeBattles.find(b => b.leader.mac === driver.mac);

                  return (
                    <React.Fragment key={driver.mac}>
                      <div className={`leaderboard-item ${pos === 0 ? 'leader' : ''}`}>
                        <div className="position">{pos + 1}</div>

                        <div className="driver-color" style={{ background: driver.color }}></div>

                        <div className="driver-info">
                          <div className="driver-name">
                            {driver.name}
                          </div>
                          <div className="driver-stats">
                            <span className="lap-count">Lap {driver.lapCount}/{totalLaps}</span>
                          </div>
                        </div>

                        {driver.photoTeamUrl && (
                          <img 
                            src={`${API_BASE}${driver.photoTeamUrl}`}
                            alt={driver.team}
                            className="team-logo-small"
                          />
                        )}

                        <div className="driver-times">
                          {pos === 0 ? (
                            <div className="gap leader-gap">LEADER</div>
                          ) : (() => {
                            const leader = leaderboard[0];
                            let gapDisplay = '';

                            if (driver.lapCount < leader.lapCount) {
                              const lapDiff = leader.lapCount - driver.lapCount;
                              gapDisplay = `+${lapDiff} ${lapDiff === 1 ? 'LAP' : 'LAPS'}`;
                            } else {
                              const sectorDiff = leader.sectorIdx - driver.sectorIdx;
                              if (sectorDiff > 0) {
                                const estimatedGap = (sectorDiff / 10).toFixed(1);
                                gapDisplay = `+${estimatedGap}s`;
                              } else {
                                gapDisplay = '+0.0s';
                              }
                            }

                            return <div className="gap">{gapDisplay}</div>;
                          })()}

                          {driver.bestLapTime && (
                            <div className="best-lap">
                              ⏱️ {formatLapTime(driver.bestLapTime)}
                            </div>
                          )}
                        </div>
                      </div>

                      {battle && (
                        <div className="battle-inline">
                          <div className="battle-line"></div>
                          <div className="battle-info">
                            <span className="battle-icon">⚔️</span>
                            <span className="battle-gap">{battle.distance.toFixed(1)}m</span>
                            <span className="battle-speed">
                              {(battle.chaser.speed - battle.leader.speed) > 0 ? '↑' : '↓'}
                            </span>
                          </div>
                          <div className="battle-line"></div>
                        </div>
                      )}
                    </React.Fragment>
                  );
                })
              )}
            </div>
          </aside>
        </div>

        <div className="race-controls">
          <button className="stop-race-btn" onClick={onStopRace}>
            ⏹️ Termina Gara
          </button>
        </div>
      </div>
    </div>
  );
}
