import React, { useState, useEffect, useRef, useCallback } from 'react';
import './Race.css';

const API_BASE = process.env.REACT_APP_API_BASE || `http://${window.location.hostname}:5000`;
const WS_URL = process.env.REACT_APP_WS_URL || `ws://${window.location.hostname}:5001`;

// Utility: calcola distanza Haversine (m)
function haversine(lat1, lon1, lat2, lon2) {
  const R = 6371000;
  const toRad = d => d * Math.PI / 180;
  const dLat = toRad(lat2 - lat1);
  const dLon = toRad(lon2 - lon1);
  const a = Math.sin(dLat / 2) ** 2 + 
            Math.cos(toRad(lat1)) * Math.cos(toRad(lat2)) * Math.sin(dLon / 2) ** 2;
  return R * 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
}

// Trova il settore più vicino al punto GPS
function findClosestSector(lat, lon, sectors) {
  let minDist = Infinity;
  let closestIdx = 0;
  sectors.forEach((s, i) => {
    const d = haversine(lat, lon, s.lat, s.lon);
    if (d < minDist) {
      minDist = d;
      closestIdx = i;
    }
  });
  return closestIdx;
}

export default function RacePage() {
  const [circuits, setCircuits] = useState([]);
  const [selectedCircuit, setSelectedCircuit] = useState(null);
  const [circuitData, setCircuitData] = useState(null);
  const [pilots, setPilots] = useState([]); // Piloti da backend
  const [raceActive, setRaceActive] = useState(false);
  const [driversState, setDriversState] = useState({}); // { MAC: { lat, lon, speed, sectorIdx, lapCount, lastLapTime, ... } }
  const [leaderboard, setLeaderboard] = useState([]); // Classifica ordinata

  const canvasRef = useRef(null);
  const wsRef = useRef(null);
  const animFrameRef = useRef(null);

  // ========== Carica circuiti disponibili ==========
  useEffect(() => {
    fetch(`${API_BASE}/api/circuits`)
      .then(r => r.json())
      .then(data => setCircuits(data))
      .catch(e => console.error('Errore caricamento circuiti:', e));
  }, []);

  // ========== Carica piloti ==========
  useEffect(() => {
    fetch(`${API_BASE}/api/pilots`)
      .then(r => r.json())
      .then(data => setPilots(data))
      .catch(e => console.error('Errore caricamento piloti:', e));
  }, []);

  // ========== Carica dettaglio circuito selezionato ==========
  useEffect(() => {
    if (!selectedCircuit) {
      setCircuitData(null);
      return;
    }
    fetch(`${API_BASE}/api/circuits/${selectedCircuit}`)
      .then(r => r.json())
      .then(data => {
        setCircuitData(data);
        console.log(`Circuito caricato: ${data.name}, settori: ${data.sectors?.length || 0}`);
      })
      .catch(e => console.error('Errore caricamento circuito:', e));
  }, [selectedCircuit]);

  // ========== WebSocket GPS Live ==========
  useEffect(() => {
    if (!raceActive) {
      if (wsRef.current) {
        wsRef.current.close();
        wsRef.current = null;
      }
      return;
    }

    const ws = new WebSocket(WS_URL);
    wsRef.current = ws;

    ws.onopen = () => console.log('[WS] Connesso al feed GPS');
    ws.onclose = () => console.log('[WS] Disconnesso');
    ws.onerror = (e) => console.error('[WS] Errore:', e);

    ws.onmessage = (event) => {
      try {
        const gps = JSON.parse(event.data);
        handleGPSUpdate(gps);
      } catch (e) {
        console.error('[WS] Parse error:', e);
      }
    };

    return () => {
      if (ws.readyState === WebSocket.OPEN) ws.close();
    };
  }, [raceActive]);

  // ========== Gestione aggiornamenti GPS ==========
  const handleGPSUpdate = useCallback((gps) => {
    if (!circuitData?.sectors) return;

    const { mac, lat, lon, speedKmh, ts } = gps;
    const sectorIdx = findClosestSector(lat, lon, circuitData.sectors);
    const totalSectors = circuitData.sectors.length;

    setDriversState(prev => {
      const existing = prev[mac] || {
        mac,
        name: `Driver ${mac.slice(-4)}`,
        lat,
        lon,
        speedKmh: 0,
        sectorIdx: sectorIdx,
        lapCount: 0,
        lastLapTime: null,
        bestLapTime: null,
        lastSectorIdx: sectorIdx,
        lapStartTime: Date.now(),
        trail: [], // ultimi N punti per scia
        color: `hsl(${Math.random() * 360}, 75%, 55%)`, // Colori più saturi
      };

      // Rileva crossing linea traguardo (settore 0)
      let newLapCount = existing.lapCount;
      let newLapTime = existing.lastLapTime;
      let newBestLap = existing.bestLapTime;

      if (existing.lastSectorIdx > totalSectors - 10 && sectorIdx < 10) {
        // Probabile completamento giro
        const lapDuration = (Date.now() - existing.lapStartTime) / 1000; // secondi
        if (lapDuration > 5) { // Evita falsi positivi
          newLapCount = existing.lapCount + 1;
          newLapTime = lapDuration;
          if (!newBestLap || lapDuration < newBestLap) {
            newBestLap = lapDuration;
          }
          console.log(`[LAP] ${mac} completato giro ${newLapCount} in ${lapDuration.toFixed(2)}s`);
        }
      }

      // Aggiorna trail (max 20 punti)
      const newTrail = [...existing.trail, { lat, lon }].slice(-20);

      return {
        ...prev,
        [mac]: {
          ...existing,
          lat,
          lon,
          speedKmh,
          sectorIdx,
          lastSectorIdx: sectorIdx,
          lapCount: newLapCount,
          lastLapTime: newLapTime,
          bestLapTime: newBestLap,
          lapStartTime: newLapCount > existing.lapCount ? Date.now() : existing.lapStartTime,
          trail: newTrail,
          updatedAt: Date.now(),
        }
      };
    });
  }, [circuitData]);

  // ========== Aggiorna classifica ==========
  useEffect(() => {
    const sorted = Object.values(driversState).sort((a, b) => {
      if (a.lapCount !== b.lapCount) return b.lapCount - a.lapCount;
      return b.sectorIdx - a.sectorIdx;
    });
    setLeaderboard(sorted);
  }, [driversState]);

  // ========== Rendering Canvas ==========
  useEffect(() => {
    if (!circuitData || !canvasRef.current) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const dpr = window.devicePixelRatio || 1;

    const resizeCanvas = () => {
      const rect = canvas.getBoundingClientRect();
      canvas.width = rect.width * dpr;
      canvas.height = rect.height * dpr;
      ctx.scale(dpr, dpr);
      canvas.style.width = `${rect.width}px`;
      canvas.style.height = `${rect.height}px`;
    };

    resizeCanvas();
    window.addEventListener('resize', resizeCanvas);

    const animate = () => {
      const w = canvas.width / dpr;
      const h = canvas.height / dpr;

      // Clear con sfondo scuro
      ctx.fillStyle = '#0a0e1a';
      ctx.fillRect(0, 0, w, h);

      // ========== GRIGLIA SFONDO (stile F1 telemetria) ==========
      ctx.strokeStyle = 'rgba(255, 255, 255, 0.03)';
      ctx.lineWidth = 1;
      const gridSize = 40;

      for (let x = 0; x < w; x += gridSize) {
        ctx.beginPath();
        ctx.moveTo(x, 0);
        ctx.lineTo(x, h);
        ctx.stroke();
      }

      for (let y = 0; y < h; y += gridSize) {
        ctx.beginPath();
        ctx.moveTo(0, y);
        ctx.lineTo(w, y);
        ctx.stroke();
      }

      if (!circuitData.pathPoints || circuitData.pathPoints.length === 0) {
        ctx.fillStyle = '#fff';
        ctx.font = '16px "Roboto Mono", monospace';
        ctx.textAlign = 'center';
        ctx.fillText('Caricamento tracciato...', w / 2, h / 2);
        animFrameRef.current = requestAnimationFrame(animate);
        return;
      }

      // ========== Calcola bounds per fitting (converti lat/lon in coordinate schermo) ==========
      const lats = circuitData.pathPoints.map(p => p.lat);
      const lons = circuitData.pathPoints.map(p => p.lon);
      const minLat = Math.min(...lats);
      const maxLat = Math.max(...lats);
      const minLon = Math.min(...lons);
      const maxLon = Math.max(...lons);

      const latRange = maxLat - minLat || 1e-5;
      const lonRange = maxLon - minLon || 1e-5;

      // Calcola fattore di scala per lat/lon → pixel, mantenendo aspect ratio
      const padding = 60;
      const usableW = w - 2 * padding;
      const usableH = h - 2 * padding;

      // Scala in base al range più grande
      const scaleX = usableW / lonRange;
      const scaleY = usableH / latRange;
      const scale = Math.min(scaleX, scaleY);

      // Centro il tracciato
      const trackW = lonRange * scale;
      const trackH = latRange * scale;
      const offsetX = (w - trackW) / 2;
      const offsetY = (h - trackH) / 2;

      // Funzione di proiezione: lat/lon → x,y canvas
      const project = (lat, lon) => {
        const x = offsetX + (lon - minLon) * scale;
        const y = offsetY + (maxLat - lat) * scale; // inverti Y (lat cresce verso l'alto)
        return { x, y };
      };

      // ========== PISTA: Disegna bordo esterno scuro (asfalto) ==========
      const trackWidthPx = Math.max(25, 40); // Larghezza pista in pixel

      ctx.strokeStyle = '#1a2332';
      ctx.lineWidth = trackWidthPx + 12;
      ctx.lineCap = 'round';
      ctx.lineJoin = 'round';
      ctx.shadowColor = 'rgba(0, 0, 0, 0.8)';
      ctx.shadowBlur = 20;

      ctx.beginPath();
      circuitData.pathPoints.forEach((p, i) => {
        const { x, y } = project(p.lat, p.lon);
        if (i === 0) ctx.moveTo(x, y);
        else ctx.lineTo(x, y);
      });
      ctx.stroke();
      ctx.shadowBlur = 0;

      // ========== PISTA: Asfalto principale ==========
      ctx.strokeStyle = '#2d3f56';
      ctx.lineWidth = trackWidthPx;
      ctx.stroke();

      // ========== PISTA: Linea centrale tratteggiata ==========
      ctx.strokeStyle = 'rgba(255, 255, 255, 0.15)';
      ctx.lineWidth = 2;
      ctx.setLineDash([10, 10]);

      ctx.beginPath();
      circuitData.pathPoints.forEach((p, i) => {
        const { x, y } = project(p.lat, p.lon);
        if (i === 0) ctx.moveTo(x, y);
        else ctx.lineTo(x, y);
      });
      ctx.stroke();
      ctx.setLineDash([]); // Reset dash

      // ========== PISTA: Bordi bianchi (vie di fuga) ==========
      ctx.strokeStyle = 'rgba(255, 255, 255, 0.4)';
      ctx.lineWidth = trackWidthPx + 2;
      ctx.globalAlpha = 0.3;
      ctx.stroke();
      ctx.globalAlpha = 1;

      // ========== LINEA TRAGUARDO (Start/Finish) - Stile F1 CLEAN ==========
      if (circuitData.pathPoints && circuitData.pathPoints.length > 5) {
        const startPt = circuitData.pathPoints[0];
        const nextPt = circuitData.pathPoints[Math.min(3, circuitData.pathPoints.length - 1)];
        const { x: x1, y: y1 } = project(startPt.lat, startPt.lon);
        const { x: x2, y: y2 } = project(nextPt.lat, nextPt.lon);

        // Calcola angolo della pista
        const angle = Math.atan2(y2 - y1, x2 - x1);

        // Perpendicular alla direzione della pista
        const perpAngle = angle + Math.PI / 2;

        // Dimensioni bandiera
        const flagWidth = trackWidthPx * 0.7;
        const flagHeight = 8;
        const checkSize = 6;

        ctx.save();
        ctx.translate(x1, y1);
        ctx.rotate(angle); // Ruota lungo la pista, non perpendicolare

        // Pattern a scacchi (semplice, senza animazione eccessiva)
        const numChecksH = Math.ceil(flagWidth / checkSize);
        const numChecksV = Math.ceil(flagHeight / checkSize);

        // Disegna scacchi
        for (let i = 0; i < numChecksH; i++) {
          for (let j = 0; j < numChecksV; j++) {
            const isWhite = (i + j) % 2 === 0;
            ctx.fillStyle = isWhite ? '#ffffff' : '#000000';
            ctx.fillRect(
              -flagWidth / 2 + i * checkSize,
              -flagHeight / 2 + j * checkSize,
              checkSize,
              checkSize
            );
          }
        }

        // Bordo rosso sottile
        ctx.strokeStyle = '#e10600';
        ctx.lineWidth = 2;
        ctx.strokeRect(
          -flagWidth / 2,
          -flagHeight / 2,
          flagWidth,
          flagHeight
        );

        ctx.restore();

        // Testo "START" sopra (piccolo e discreto)
        ctx.fillStyle = 'rgba(255, 255, 255, 0.8)';
        ctx.font = 'bold 10px "Roboto Mono", monospace';
        ctx.textAlign = 'center';
        ctx.fillText('START', x1, y1 - flagHeight - 8);
      }

      // ========== SETTORI: Visualizza marker ogni N settori ==========
      if (circuitData.sectors && circuitData.sectors.length > 50) {
        const sectorInterval = Math.floor(circuitData.sectors.length / 8); // 8 marker
        ctx.fillStyle = 'rgba(225, 6, 0, 0.6)';
        ctx.font = 'bold 10px "Roboto Mono", monospace';
        ctx.textAlign = 'center';

        for (let i = 0; i < circuitData.sectors.length; i += sectorInterval) {
          if (i === 0) continue; // Skip start/finish
          const sector = circuitData.sectors[i];
          const { x, y } = project(sector.lat, sector.lon);

          // Pallino settore
          ctx.beginPath();
          ctx.arc(x, y, 4, 0, Math.PI * 2);
          ctx.fill();

          // Numero settore
          ctx.fillStyle = '#fff';
          ctx.fillText(`S${i}`, x, y - 10);
          ctx.fillStyle = 'rgba(225, 6, 0, 0.6)';
        }
      }

      // ========== PRIMA: Disegna tutte le scie (dietro i piloti) ==========
      const driversArray = Object.values(driversState);

      driversArray.forEach(driver => {
        const { lat, lon, trail, color } = driver;

        if (trail && trail.length > 1) {
          // Gradiente fade-out sulla scia
          ctx.strokeStyle = color;
          ctx.lineWidth = 3;
          ctx.lineCap = 'round';
          ctx.lineJoin = 'round';

          for (let i = 0; i < trail.length - 1; i++) {
            const alpha = (i / trail.length) * 0.4; // Fade da 0 a 0.4
            ctx.globalAlpha = alpha;

            const pt1 = trail[i];
            const pt2 = trail[i + 1];
            const { x: x1, y: y1 } = project(pt1.lat, pt1.lon);
            const { x: x2, y: y2 } = project(pt2.lat, pt2.lon);

            ctx.beginPath();
            ctx.moveTo(x1, y1);
            ctx.lineTo(x2, y2);
            ctx.stroke();
          }
          ctx.globalAlpha = 1;
        }
      });

      // ========== DOPO: Disegna piloti ordinati per posizione (ultimo → primo) ==========
      // Ordina: chi è dietro viene disegnato prima, chi è davanti ultimo (sopra)
      const sortedDrivers = [...driversArray].sort((a, b) => {
        // Ordina per giri + settori (chi è più indietro prima)
        if (a.lapCount !== b.lapCount) return a.lapCount - b.lapCount;
        return a.sectorIdx - b.sectorIdx;
      });

      sortedDrivers.forEach((driver, index) => {
        const { lat, lon, color, speedKmh, mac } = driver;
        const { x, y } = project(lat, lon);

        const isLeader = index === sortedDrivers.length - 1; // Ultimo nell'array = primo in gara
        const pilotSize = isLeader ? 7 : 6; // Ridotti: leader 7px, altri 6px
        const borderSize = 1.5; // Bordo più sottile

        // ===== Rileva battaglia (pilota molto vicino a quello dietro) =====
        let isBattle = false;
        if (index > 0) {
          const driverBehind = sortedDrivers[index - 1];
          const dist = haversine(lat, lon, driverBehind.lat, driverBehind.lon);
          if (dist < 15) { // Meno di 15 metri
            isBattle = true;
          }
        }

        // ===== Ombra per profondità =====
        ctx.shadowColor = 'rgba(0, 0, 0, 0.6)';
        ctx.shadowBlur = 8;
        ctx.shadowOffsetX = 2;
        ctx.shadowOffsetY = 2;

        // ===== Indicatore battaglia (lampo rosso pulsante) =====
        if (isBattle) {
          const battlePulse = 0.7 + 0.3 * Math.sin(Date.now() / 100);
          ctx.fillStyle = `rgba(225, 6, 0, ${battlePulse * 0.6})`;
          ctx.beginPath();
          ctx.arc(x, y, 18, 0, Math.PI * 2);
          ctx.fill();
        }

        // ===== Alone esterno (solo leader) =====
        if (isLeader) {
          const pulse = 1 + 0.15 * Math.sin(Date.now() / 250);
          ctx.fillStyle = color;
          ctx.globalAlpha = 0.4;
          ctx.beginPath();
          ctx.arc(x, y, 16 * pulse, 0, Math.PI * 2);
          ctx.fill();
          ctx.globalAlpha = 1;
        }

        // ===== Cerchio pilota con bordo bianco spesso =====
        // Bordo esterno bianco
        ctx.fillStyle = '#ffffff';
        ctx.beginPath();
        ctx.arc(x, y, pilotSize + 2, 0, Math.PI * 2);
        ctx.fill();

        // Cerchio interno colorato
        ctx.fillStyle = color;
        ctx.beginPath();
        ctx.arc(x, y, pilotSize, 0, Math.PI * 2);
        ctx.fill();

        // Reset ombra
        ctx.shadowColor = 'transparent';
        ctx.shadowBlur = 0;
        ctx.shadowOffsetX = 0;
        ctx.shadowOffsetY = 0;

        // ===== Velocità sopra (con sfondo per leggibilità) =====
        const speedText = `${Math.round(speedKmh)}`;
        ctx.font = 'bold 12px "Roboto Mono", monospace';
        ctx.textAlign = 'center';
        ctx.textBaseline = 'middle';

        // Sfondo scuro dietro il numero
        const textMetrics = ctx.measureText(speedText);
        const textWidth = textMetrics.width;
        const textHeight = 14;

        ctx.fillStyle = 'rgba(0, 0, 0, 0.7)';
        ctx.fillRect(
          x - textWidth / 2 - 3,
          y - pilotSize - 20 - textHeight / 2,
          textWidth + 6,
          textHeight
        );

        // Numero velocità
        ctx.fillStyle = '#fff';
        ctx.fillText(speedText, x, y - pilotSize - 20);

        // ===== Nome pilota (solo per leader) =====
        if (isLeader) {
          const driverName = driver.name || `#${mac.slice(-4)}`;
          ctx.font = 'bold 10px "Roboto Mono", monospace';
          const nameMetrics = ctx.measureText(driverName);

          // Sfondo
          ctx.fillStyle = 'rgba(225, 6, 0, 0.9)';
          ctx.fillRect(
            x - nameMetrics.width / 2 - 4,
            y + pilotSize + 8,
            nameMetrics.width + 8,
            16
          );

          // Testo
          ctx.fillStyle = '#fff';
          ctx.fillText(driverName, x, y + pilotSize + 16);
        }
      });

      animFrameRef.current = requestAnimationFrame(animate);
    };

    animate();

    return () => {
      window.removeEventListener('resize', resizeCanvas);
      if (animFrameRef.current) cancelAnimationFrame(animFrameRef.current);
    };
  }, [circuitData, driversState]);

  // ========== Formatta tempo giro (s → mm:ss.ms) ==========
  const formatLapTime = (seconds) => {
    if (!seconds) return '--:--.---';
    const mins = Math.floor(seconds / 60);
    const secs = (seconds % 60).toFixed(3);
    return `${mins}:${secs.padStart(6, '0')}`;
  };

  // ========== UI ==========
  return (
    <div className="race-page">
      {/* Header con logo stile F1 */}
      <header className="race-header">
        <div className="race-header-left">
          <div className="race-logo">🏁 RACESENSE</div>
          <div className="race-title">LIVE RACE</div>
        </div>
        <div className="race-header-right">
          {raceActive && circuitData && (
            <div className="circuit-badge">
              <span className="circuit-icon">🛣️</span>
              <span className="circuit-name">{circuitData.name}</span>
            </div>
          )}
        </div>
      </header>

      {!raceActive ? (
        // ========== Setup gara ==========
        <div className="race-setup">
          <div className="setup-card">
            <h2>⚙️ Configura Gara</h2>

            <div className="form-group">
              <label>Seleziona Circuito</label>
              <select 
                value={selectedCircuit || ''} 
                onChange={e => setSelectedCircuit(e.target.value)}
                className="circuit-select"
              >
                <option value="">-- Scegli un tracciato --</option>
                {circuits.map(c => (
                  <option key={c.id} value={c.id}>
                    {c.name} ({c.lengthMeters?.toFixed(0)}m, {c.points} punti)
                  </option>
                ))}
              </select>
            </div>

            {circuitData && (
              <div className="circuit-preview">
                <h3>📊 Info Circuito</h3>
                <div className="circuit-stats">
                  <div className="stat">
                    <span className="stat-label">Lunghezza</span>
                    <span className="stat-value">{circuitData.stats?.lengthMeters?.toFixed(0)} m</span>
                  </div>
                  <div className="stat">
                    <span className="stat-label">Larghezza</span>
                    <span className="stat-value">{circuitData.params?.widthMeters || 6} m</span>
                  </div>
                  <div className="stat">
                    <span className="stat-label">Settori</span>
                    <span className="stat-value">{circuitData.sectors?.length || 0}</span>
                  </div>
                </div>
              </div>
            )}

            <button 
              className="start-race-btn"
              disabled={!selectedCircuit}
              onClick={() => setRaceActive(true)}
            >
              🚦 AVVIA GARA
            </button>
          </div>
        </div>
      ) : (
        // ========== Vista gara live ==========
        <div className="race-live">
          <div className="race-main">
            {/* Canvas pista */}
            <div className="track-container">
              <canvas ref={canvasRef} className="track-canvas" />

              {/* Overlay info */}
              <div className="track-overlay">
                <div className="live-indicator">
                  <span className="live-dot"></span>
                  LIVE
                </div>

                {/* Info tracciato */}
                {circuitData && (
                  <div className="track-info">
                    <div className="track-stat">
                      <span className="stat-icon">📏</span>
                      <span className="stat-text">{circuitData.stats?.lengthMeters?.toFixed(0)}m</span>
                    </div>
                    <div className="track-stat">
                      <span className="stat-icon">🛣️</span>
                      <span className="stat-text">{circuitData.params?.widthMeters || 6}m wide</span>
                    </div>
                    <div className="track-stat">
                      <span className="stat-icon">📍</span>
                      <span className="stat-text">{circuitData.sectors?.length || 0} sectors</span>
                    </div>
                  </div>
                )}
              </div>
            </div>

            {/* Classifica laterale stile F1 */}
            <aside className="leaderboard">
              <div className="leaderboard-header">
                <h3>🏆 CLASSIFICA</h3>
                <div className="leaderboard-subtitle">
                  {leaderboard.length} Piloti in pista
                </div>
              </div>

              <div className="leaderboard-list">
                {leaderboard.length === 0 ? (
                  <div className="no-drivers">
                    <p>⏳ In attesa di dati GPS...</p>
                    <small>Avvia il simulatore o connetti dispositivi GPS</small>
                  </div>
                ) : (
                  leaderboard.map((driver, pos) => {
                    // Rileva battaglia: pilota molto vicino a quello davanti
                    const isBattle = pos > 0 && (() => {
                      const driverAhead = leaderboard[pos - 1];
                      const dist = haversine(driver.lat, driver.lon, driverAhead.lat, driverAhead.lon);
                      return dist < 15; // Meno di 15 metri
                    })();

                    return (
                      <div 
                        key={driver.mac} 
                        className={`leaderboard-item ${pos === 0 ? 'leader' : ''} ${isBattle ? 'battle' : ''}`}
                      >
                        <div className="position">{pos + 1}</div>

                        <div className="driver-color" style={{ background: driver.color }}></div>

                        <div className="driver-info">
                          <div className="driver-name">
                            {driver.name}
                            {pos === 0 && <span className="leader-badge">👑</span>}
                            {isBattle && <span className="battle-badge">⚔️</span>}
                          </div>
                          <div className="driver-stats">
                            <span className="lap-count">Giro {driver.lapCount}</span>
                            <span className="sector">S{driver.sectorIdx}</span>
                          </div>
                        </div>

                        <div className="driver-times">
                          <div className="speed">{Math.round(driver.speedKmh)} km/h</div>
                          {driver.bestLapTime && (
                            <div className="best-lap">
                              ⏱️ {formatLapTime(driver.bestLapTime)}
                            </div>
                          )}
                        </div>
                      </div>
                    );
                  })
                )}
              </div>
            </aside>
          </div>

          {/* Controlli gara */}
          <div className="race-controls">
            <button 
              className="stop-race-btn"
              onClick={() => {
                setRaceActive(false);
                setDriversState({});
                setLeaderboard([]);
              }}
            >
              ⏹️ Termina Gara
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
