import React, { useState, useEffect } from "react";

export default function Championships() {
    const [championships, setChampionships] = useState([]);
    const [name, setName] = useState("");
    const [photoFile, setPhotoFile] = useState(null);
    const [photoPreview, setPhotoPreview] = useState(null);
    const [error, setError] = useState("");
    const [loading, setLoading] = useState(false);

    // Nuovi stati locali per le formule (input per campionato)
    const [newFormulaByChamp, setNewFormulaByChamp] = useState({}); // { [champId]: "Formula 2" }
    const [savingFormulaId, setSavingFormulaId] = useState(null);

    const API_BASE = process.env.REACT_APP_API_BASE || `http://${window.location.hostname}:5000`;

    // === Load campionati ===
    const fetchChampionships = () => {
        fetch(`${API_BASE}/api/championships`)
            .then((res) => res.json())
            .then((list) => {
                // garantisci che ogni item abbia almeno un array formule
                const normalized = (list || []).map((c) => ({
                    ...c,
                    formulas: Array.isArray(c.formulas) ? c.formulas : [],
                }));
                setChampionships(normalized);
            })
            .catch(() => setChampionships([]));
    };

    useEffect(() => {
        fetchChampionships();
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [API_BASE]);

    // === Dropzone ===
    const handleFile = (file) => {
        if (!file) return;
        setPhotoFile(file);
        const reader = new FileReader();
        reader.onloadend = () => setPhotoPreview(reader.result);
        reader.readAsDataURL(file);
    };
    const handleDrop = (e) => {
        e.preventDefault();
        e.stopPropagation();
        const file = e.dataTransfer.files[0];
        if (file) handleFile(file);
    };

    // === Crea campionato ===
    const handleAddChampionship = async (e) => {
        e.preventDefault();
        setError("");
        if (!name.trim()) {
            setError("Inserisci il nome del campionato");
            return;
        }

        setLoading(true);
        const formData = new FormData();
        formData.append("name", name);
        if (photoFile) formData.append("photo", photoFile);

        try {
            const res = await fetch(`${API_BASE}/api/championships`, {
                method: "POST",
                body: formData,
            });
            if (!res.ok) throw new Error(`Errore HTTP ${res.status}`);
            const data = await res.json();
            const normalized = (data || []).map((c) => ({
                ...c,
                formulas: Array.isArray(c.formulas) ? c.formulas : [],
            }));
            setChampionships(normalized);
            setName("");
            setPhotoFile(null);
            setPhotoPreview(null);
        } catch (err) {
            console.error("Errore:", err);
            setError("Errore durante il salvataggio del campionato.");
        } finally {
            setLoading(false);
        }
    };

    // === Formule: add ===
    const handleAddFormula = async (champId) => {
        const label = (newFormulaByChamp[champId] || "").trim();
        if (!label) return;

        setSavingFormulaId(champId);
        try {
            const res = await fetch(`${API_BASE}/api/championships/${encodeURIComponent(champId)}/formulas`, {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ label }),
            });
            if (!res.ok) throw new Error(`Errore HTTP ${res.status}`);
            // L‚Äôendpoint restituisce il campionato aggiornato: aggiorna solo quello
            const updated = await res.json();
            setChampionships((prev) =>
                prev.map((c) => (c.id === updated.id ? { ...updated, formulas: updated.formulas || [] } : c))
            );
            setNewFormulaByChamp((prev) => ({ ...prev, [champId]: "" }));
        } catch (e) {
            console.error(e);
            alert("Errore durante l'aggiunta della formula");
        } finally {
            setSavingFormulaId(null);
        }
    };

    // === Formule: delete ===
    const handleDeleteFormula = async (champId, formulaId) => {
        if (!window.confirm("Eliminare questa formula?")) return;
        try {
            const res = await fetch(
                `${API_BASE}/api/championships/${encodeURIComponent(champId)}/formulas/${encodeURIComponent(formulaId)}`,
                { method: "DELETE" }
            );
            if (!res.ok) throw new Error(`Errore HTTP ${res.status}`);
            const updated = await res.json();
            setChampionships((prev) =>
                prev.map((c) => (c.id === updated.id ? { ...updated, formulas: updated.formulas || [] } : c))
            );
        } catch (e) {
            console.error(e);
            alert("Errore durante l'eliminazione della formula");
        }
    };

    return (
        <div className="main small-top">
            <h1 className="section-title">Campionati</h1>
            <p className="subtitle">
                Crea un nuovo campionato, assegna una foto e gestisci le <b>Formule</b> (sottocategorie) per ogni campionato.
            </p>

            {/* === FORM CREAZIONE CAMPIONATO === */}
            <form className="pilot-form" onSubmit={handleAddChampionship}>
                <div className="form-row">
                    <div className="form-col">
                        <label>Nome del Campionato *</label>
                        <input
                            type="text"
                            className="input"
                            placeholder="Es. Formula GP Italia"
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                        />
                    </div>

                    <div className="form-col">
                        <label>Foto Campionato (opzionale)</label>
                        <div
                            className={`dz ${photoPreview ? "has-preview" : ""}`}
                            onDragOver={(e) => e.preventDefault()}
                            onDrop={handleDrop}
                            onClick={() => document.getElementById("fileInput").click()}
                        >
                            {!photoPreview ? (
                                <div className="dz-inner">
                                    <span className="dz-label">Trascina qui o clicca per caricare</span>
                                    <span className="dz-hint">Formato .jpg, .png, .webp</span>
                                </div>
                            ) : (
                                <div className="dz-preview">
                                    <img src={photoPreview} alt="Preview" />
                                    <span className="dz-filename">{photoFile?.name}</span>
                                </div>
                            )}
                            <input
                                id="fileInput"
                                type="file"
                                accept="image/*"
                                style={{ display: "none" }}
                                onChange={(e) => handleFile(e.target.files[0])}
                            />
                        </div>
                    </div>

                    <div className="form-col" style={{ display: "flex", alignItems: "end" }}>
                        <button type="submit" className="btn-primary" disabled={loading}>
                            {loading ? "Salvataggio..." : "Aggiungi Campionato"}
                        </button>
                    </div>
                </div>

                {error && <div className="error">{error}</div>}
            </form>

            {/* === LISTA CAMPIONATI === */}
            <h2 className="section-subtitle">Campionati salvati</h2>

            {championships.length === 0 ? (
                <div className="cr-empty">
                    <div className="cr-empty-emoji">üèÅ</div>
                    <div className="cr-empty-title">Nessun campionato creato</div>
                    <p className="muted">Aggiungine uno sopra per iniziare.</p>
                </div>
            ) : (
                <div className="cr-grid">
                    {championships.map((ch) => (
                        <div key={ch.id} className="cr-card">
                            <div className="cr-card-head">
                                <h3 className="cr-title">{ch.name}</h3>
                                <span className="muted small">
                                    {ch.createdAt ? new Date(ch.createdAt).toLocaleDateString() : ""}
                                </span>
                            </div>

                            {ch.photo ? (
                                <img
                                    src={`${API_BASE}${ch.photo}`}
                                    alt={ch.name}
                                    className="cr-mini"
                                    style={{ objectFit: "cover" }}
                                />
                            ) : (
                                <div className="cr-mini"></div>
                            )}

                            {/* === Sezione Formule === */}
                            <div>
                                <div className="cr-meta" style={{ marginTop: 8 }}>
                                    {ch.formulas?.length ? (
                                        ch.formulas.map((f) => (
                                            <span key={f.id} className="pill" title="Formula">
                                                {f.label}
                                                <button
                                                    type="button"
                                                    onClick={() => handleDeleteFormula(ch.id, f.id)}
                                                    style={{
                                                        marginLeft: 8,
                                                        background: "transparent",
                                                        border: "none",
                                                        color: "var(--error)",
                                                        cursor: "pointer",
                                                        fontWeight: 900,
                                                    }}
                                                    aria-label="Elimina formula"
                                                    title="Elimina formula"
                                                >
                                                    √ó
                                                </button>
                                            </span>
                                        ))
                                    ) : (
                                        <span className="pill ghost">Nessuna formula</span>
                                    )}
                                </div>

                                {/* Input aggiunta formula */}
                                <div className="form-row" style={{ gridTemplateColumns: "1fr auto", marginTop: 10 }}>
                                    <div className="form-col">
                                        <label>Nuova formula</label>
                                        <input
                                            type="text"
                                            className="input"
                                            placeholder="Es. Formula 2, Junior, Endurance‚Ä¶"
                                            value={newFormulaByChamp[ch.id] || ""}
                                            onChange={(e) =>
                                                setNewFormulaByChamp((prev) => ({ ...prev, [ch.id]: e.target.value }))
                                            }
                                        />
                                    </div>
                                    <div className="form-col" style={{ display: "flex", alignItems: "end" }}>
                                        <button
                                            type="button"
                                            className="btn-ghost"
                                            onClick={() => handleAddFormula(ch.id)}
                                            disabled={!((newFormulaByChamp[ch.id] || "").trim()) || savingFormulaId === ch.id}
                                        >
                                            {savingFormulaId === ch.id ? "Aggiungo‚Ä¶" : "Aggiungi Formula"}
                                        </button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}
