import React, { useState, useEffect, useRef } from 'react';
import './RaceSetup.css';

const API_BASE = process.env.REACT_APP_API_BASE || `http://${window.location.hostname}:5000`;
const WS_URL = process.env.REACT_APP_WS_URL || `ws://${window.location.hostname}:5001`;

export default function RaceSetup({ onStartRace }) {
  const [circuits, setCircuits] = useState([]);
  const [pilots, setPilots] = useState([]);
  const [selectedCircuit, setSelectedCircuit] = useState(null);
  const [circuitData, setCircuitData] = useState(null);
  const [totalLaps, setTotalLaps] = useState(10);

  // Device attivi rilevati in tempo reale
  const [activeDevices, setActiveDevices] = useState({}); // { MAC: { lastSeen, lat, lon, speed } }
  const [deviceAssignments, setDeviceAssignments] = useState({}); // { MAC: pilotId }

  const wsRef = useRef(null);
  const deviceTimeoutRef = useRef({});

  // ========== Carica circuiti e piloti ==========
  useEffect(() => {
    fetch(`${API_BASE}/api/circuits`)
      .then(r => r.json())
      .then(data => setCircuits(data))
      .catch(e => console.error('Errore caricamento circuiti:', e));

    fetch(`${API_BASE}/api/pilots`)
      .then(r => r.json())
      .then(data => setPilots(data))
      .catch(e => console.error('Errore caricamento piloti:', e));
  }, []);

  // ========== Carica dettagli circuito selezionato ==========
  useEffect(() => {
    if (!selectedCircuit) return;

    fetch(`${API_BASE}/api/circuits/${selectedCircuit}`)
      .then(r => r.json())
      .then(data => setCircuitData(data))
      .catch(e => console.error('Errore caricamento circuito:', e));
  }, [selectedCircuit]);

  // ========== WebSocket per rilevare device attivi ==========
  useEffect(() => {
    const ws = new WebSocket(WS_URL);
    wsRef.current = ws;

    ws.onopen = () => {
      console.log('[RaceSetup] WebSocket connesso - in ascolto per device attivi');
    };

    ws.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data);

        // Formato: MAC/LAT/LON/SATS/QUAL/SPEED/TIMESTAMP/TEMP
        if (data.raw) {
          const parts = data.raw.split('/');
          if (parts.length >= 6) {
            const mac = parts[0];
            const lat = parseFloat(parts[1]);
            const lon = parseFloat(parts[2]);
            const speed = parseFloat(parts[5]);

            setActiveDevices(prev => ({
              ...prev,
              [mac]: {
                lastSeen: Date.now(),
                lat,
                lon,
                speed,
                raw: data.raw
              }
            }));

            // Resetta timeout per questo device
            if (deviceTimeoutRef.current[mac]) {
              clearTimeout(deviceTimeoutRef.current[mac]);
            }

            // Rimuovi device se inattivo per 5 secondi
            deviceTimeoutRef.current[mac] = setTimeout(() => {
              setActiveDevices(prev => {
                const updated = { ...prev };
                delete updated[mac];
                return updated;
              });
            }, 5000);
          }
        }
      } catch (err) {
        console.error('Errore parsing GPS:', err);
      }
    };

    ws.onerror = (err) => console.error('[RaceSetup] WebSocket errore:', err);
    ws.onclose = () => console.log('[RaceSetup] WebSocket chiuso');

    return () => {
      Object.values(deviceTimeoutRef.current).forEach(clearTimeout);
      ws.close();
    };
  }, []);

  // ========== Assegna pilota a device ==========
  const assignPilotToDevice = (mac, pilotId) => {
    setDeviceAssignments(prev => ({
      ...prev,
      [mac]: pilotId
    }));
  };

  // ========== Avvia gara ==========
  const handleStartRace = () => {
    if (!selectedCircuit) {
      alert('⚠️ Seleziona un circuito!');
      return;
    }

    const assignedDevices = Object.keys(deviceAssignments).filter(mac => deviceAssignments[mac]);

    if (assignedDevices.length === 0) {
      alert('⚠️ Assegna almeno un pilota a un device attivo!');
      return;
    }

    // Prepara configurazione gara
    const raceConfig = {
      circuit: selectedCircuit,
      circuitData,
      totalLaps,
      assignments: deviceAssignments,
      pilots: pilots.filter(p => Object.values(deviceAssignments).includes(p.id)),
      activeDevices: assignedDevices
    };

    onStartRace(raceConfig);
  };

  const activeMacs = Object.keys(activeDevices);
  const assignedCount = Object.values(deviceAssignments).filter(Boolean).length;

  return (
    <div className="race-setup-page">
      <header className="setup-header">
        <div className="setup-logo">🏁 RACESENSE</div>
        <div className="setup-title">CONFIGURAZIONE GARA</div>
      </header>

      <div className="setup-content">
        {/* Selezione Circuito */}
        <section className="setup-section circuit-section">
          <h2 className="section-title">
            <span className="section-icon">🛣️</span>
            CIRCUITO
          </h2>

          <div className="circuits-grid">
            {circuits.map(circuit => (
              <div 
                key={circuit.id}
                className={`circuit-card ${selectedCircuit === circuit.id ? 'selected' : ''}`}
                onClick={() => setSelectedCircuit(circuit.id)}
              >
                <div className="circuit-header">
                  <span className="circuit-name">{circuit.name}</span>
                  {selectedCircuit === circuit.id && <span className="check">✓</span>}
                </div>
                <div className="circuit-stats">
                  <div className="stat">
                    <span className="stat-icon">📏</span>
                    <span>{(circuit.lengthMeters || 0).toFixed(0)}m</span>
                  </div>
                  <div className="stat">
                    <span className="stat-icon">📍</span>
                    <span>{circuit.points || 0} pts</span>
                  </div>
                </div>
              </div>
            ))}
          </div>

          {circuitData && (
            <div className="circuit-details">
              <div className="detail">
                <span>Lunghezza:</span>
                <strong>{circuitData.stats?.lengthMeters?.toFixed(0)}m</strong>
              </div>
              <div className="detail">
                <span>Settori:</span>
                <strong>{circuitData.sectors?.length || 0}</strong>
              </div>
              <div className="detail">
                <span>Larghezza:</span>
                <strong>{circuitData.params?.widthMeters || 6}m</strong>
              </div>
            </div>
          )}
        </section>

        {/* Device Attivi e Assegnazioni */}
        <section className="setup-section devices-section">
          <h2 className="section-title">
            <span className="section-icon">📡</span>
            DEVICE ATTIVI ({activeMacs.length})
            <span className="live-indicator">
              <span className="live-dot"></span>
              LIVE
            </span>
          </h2>

          {activeMacs.length === 0 ? (
            <div className="no-devices">
              <div className="no-devices-icon">📡</div>
              <p>Nessun device GPS attivo</p>
              <small>Avvia il simulatore o connetti dispositivi GPS</small>
            </div>
          ) : (
            <div className="devices-list">
              {activeMacs.map(mac => {
                const device = activeDevices[mac];
                const assignedPilotId = deviceAssignments[mac];
                const assignedPilot = pilots.find(p => p.id === assignedPilotId);

                return (
                  <div key={mac} className={`device-card ${assignedPilotId ? 'assigned' : ''}`}>
                    <div className="device-info">
                      <div className="device-header">
                        <span className="device-mac">{mac}</span>
                        <span className="device-signal">📶</span>
                      </div>
                      <div className="device-data">
                        <span className="device-stat">📍 {device.lat.toFixed(6)}, {device.lon.toFixed(6)}</span>
                        <span className="device-stat">⚡ {device.speed.toFixed(1)} km/h</span>
                      </div>
                    </div>

                    <div className="device-assignment">
                      <select 
                        className="pilot-select"
                        value={assignedPilotId || ''}
                        onChange={(e) => assignPilotToDevice(mac, e.target.value)}
                      >
                        <option value="">-- Assegna Pilota --</option>
                        {pilots.map(pilot => (
                          <option key={pilot.id} value={pilot.id}>
                            {pilot.name} {pilot.surname} ({pilot.team})
                          </option>
                        ))}
                      </select>

                      {assignedPilot && (
                        <div className="assigned-pilot">
                          <div 
                            className="pilot-color" 
                            style={{ background: assignedPilot.color || '#fff' }}
                          ></div>
                          <span className="pilot-name">
                            {assignedPilot.name} {assignedPilot.surname}
                          </span>
                        </div>
                      )}
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </section>

        {/* Configurazione Giri */}
        <section className="setup-section laps-section">
          <h2 className="section-title">
            <span className="section-icon">🏁</span>
            NUMERO GIRI
          </h2>

          <div className="laps-selector">
            <button 
              className="lap-btn"
              onClick={() => setTotalLaps(Math.max(1, totalLaps - 1))}
            >
              −
            </button>
            <div className="laps-display">
              <div className="laps-number">{totalLaps}</div>
              <div className="laps-label">GIRI</div>
            </div>
            <button 
              className="lap-btn"
              onClick={() => setTotalLaps(totalLaps + 1)}
            >
              +
            </button>
          </div>
        </section>

        {/* Pulsante Start */}
        <button 
          className="start-btn"
          disabled={!selectedCircuit || assignedCount === 0}
          onClick={handleStartRace}
        >
          <span className="start-icon">🚦</span>
          <span className="start-text">AVVIA GARA</span>
          <span className="start-info">
            {selectedCircuit && assignedCount > 0
              ? `${assignedCount} ${assignedCount === 1 ? 'pilota' : 'piloti'} × ${totalLaps} giri`
              : 'Configura gara per iniziare'
            }
          </span>
        </button>
      </div>
    </div>
  );
}
