import React, { useEffect, useMemo, useRef, useState } from 'react';
import '../App.css';

const API_BASE = process.env.REACT_APP_API_BASE || `http://${window.location.hostname}:5000`;
const WS_URL = process.env.REACT_APP_WS_URL || `ws://${window.location.hostname}:5001`;

/* ===== utils ===== */
function formatLap(sec) {
  if (!sec && sec !== 0) return '—';
  const m = Math.floor(sec / 60);
  const s = (sec % 60).toFixed(3).padStart(6, '0');
  return `${m}:${s}`;
}

/* ===== component ===== */
export default function RaceLive({ raceConfig, onStopRace }) {
  const [serverState, setServerState] = useState(null);       // circuito + summary iniziali
  const [summary, setSummary] = useState(null);               // aggiornato dal WS (classifica server-side)
  const [raceStatus, setRaceStatus] = useState('IN CORSO');   // mirror lato client
  const [pilotsIndex, setPilotsIndex] = useState({});         // pilotId -> pilot object
  const canvasRef = useRef(null);
  const zoomRef = useRef(1);
  const panRef = useRef({ x: 0, y: 0 });
  const isDraggingRef = useRef(false);
  const lastMouseRef = useRef({ x: 0, y: 0 });
  const animRef = useRef(null);
  const dprRef = useRef(1);
  const circuitRef = useRef(null);

  const driverPositionsRef = useRef({}); // mac -> { lat, lon, tag, color, photoTeamUrl }
  const summaryRef = useRef(null);       // ultima summary dal server


  // ===== fetch stato iniziale (se entro senza config locale) =====
  useEffect(() => {
    let alive = true;
    setSummary(j.summary);
    summaryRef.current = j.summary;   // << aggiungi questa riga
    setRaceStatus(j.summary?.status || 'IN CORSO');

    const init = async () => {
      if (raceConfig && raceConfig.serverMode) {
        const r = await fetch(`${API_BASE}/api/race/state`);
        const j = await r.json();
        if (!alive) return;
        if (!j.active) {
          setServerState(null);
          return;
        }
        setServerState(j);
        setSummary(j.summary);
        setRaceStatus(j.summary?.status || 'IN CORSO');
        setPilotsIndex(Object.fromEntries((j.pilots || []).map(p => [p.id, p])));
        circuitRef.current = j.circuit;
      } else {
        // anche se raceConfig è null, provo comunque a joinare se c'è una gara
        const r = await fetch(`${API_BASE}/api/race/state`);
        const j = await r.json();
        if (!alive) return;
        if (j.active) {
          setServerState(j);
          setSummary(j.summary);
          setRaceStatus(j.summary?.status || 'IN CORSO');
          setPilotsIndex(Object.fromEntries((j.pilots || []).map(p => [p.id, p])));
          circuitRef.current = j.circuit;
        }
      }
    };
    init();
    return () => { alive = false; };
  }, [raceConfig]);

  // ===== WebSocket: ricevo GPS per canvas + summary per classifica =====
  useEffect(() => {
    const ws = new WebSocket(WS_URL);
    const driverPositions = {}; // mac -> {lat,lon,tag,color,teamLogo}

    ws.onmessage = (ev) => {
      try {
        const msg = JSON.parse(ev.data);
        if (msg?.type === 'race') {
          setSummary(msg.payload);
          setRaceStatus(msg.payload?.status || 'IN CORSO');
        } else if (msg?.type === 'gps' || (msg?.mac && msg?.lat && msg?.lon)) {
          // memorizzo posizione corrente per il draw loop
          const mac = msg.mac;
          // Provo a recuperare info pilota dalla summary per mostrare tag e colore
          const d = (summary?.leaderboard || []).find(x => x.mac === mac);
          driverPositions[mac] = {
            lat: msg.lat, lon: msg.lon,
            tag: d?.tag || 'PIL',
            color: d?.color || '#C0FF03',
            photoTeamUrl: d?.photoTeamUrl || null
          };
        }
      } catch { }
    };
    ws.onerror = e => console.error('[WS]', e);

    // ===== Canvas draw loop (solo render, niente calcoli di classifica) =====
    const canvas = canvasRef.current;
    if (!canvas) return () => ws.close();
    const ctx = canvas.getContext('2d');
    const dpr = window.devicePixelRatio || 1;
    dprRef.current = dpr;

    const ensureSize = () => {
      const parent = canvas.parentElement;
      const w = parent.clientWidth || 800;
      const h = parent.clientHeight || 520;
      if (canvas.width !== Math.floor(w * dpr) || canvas.height !== Math.floor(h * dpr)) {
        canvas.width = Math.floor(w * dpr);
        canvas.height = Math.floor(h * dpr);
        ctx.setTransform(dpr, 0, 0, dpr, 0, 0);
      }
    };
    ensureSize();
    const onResize = () => ensureSize();
    window.addEventListener('resize', onResize);

    const wheel = (e) => { e.preventDefault(); zoomRef.current = Math.max(0.5, Math.min(5, zoomRef.current * (e.deltaY > 0 ? 0.9 : 1.1))); };
    const mousedown = (e) => { isDraggingRef.current = true; lastMouseRef.current = { x: e.clientX, y: e.clientY }; canvas.style.cursor = 'grabbing'; };
    const mousemove = (e) => {
      if (!isDraggingRef.current) return;
      const dx = e.clientX - lastMouseRef.current.x;
      const dy = e.clientY - lastMouseRef.current.y;
      panRef.current.x += dx; panRef.current.y += dy;
      lastMouseRef.current = { x: e.clientX, y: e.clientY };
    };
    const mouseup = () => { isDraggingRef.current = false; canvas.style.cursor = 'grab'; };
    canvas.addEventListener('wheel', wheel, { passive: false });
    canvas.addEventListener('mousedown', mousedown);
    canvas.addEventListener('mousemove', mousemove);
    canvas.addEventListener('mouseup', mouseup);
    canvas.addEventListener('mouseleave', mouseup);
    canvas.style.cursor = 'grab';

    // proiezione
    const projectFactory = () => {
      const circuit = circuitRef.current;
      if (!circuit?.sectors?.length) return null;
      const lats = circuit.sectors.map(s => s.lat);
      const lons = circuit.sectors.map(s => s.lon);
      const minLat = Math.min(...lats), maxLat = Math.max(...lats);
      const minLon = Math.min(...lons), maxLon = Math.max(...lons);
      const centerLat = (minLat + maxLat) / 2;
      const centerLon = (minLon + maxLon) / 2;
      const R = 6371000;
      const centerLatRad = centerLat * Math.PI / 180;
      const dxList = lons.map(l => (l - centerLon) * Math.PI / 180 * R * Math.cos(centerLatRad));
      const dyList = lats.map(l => (l - centerLat) * Math.PI / 180 * R);
      const maxDx = Math.max(...dxList.map(Math.abs));
      const maxDy = Math.max(...dyList.map(Math.abs));
      const padding = 40;
      return (lat, lon, w, h) => {
        const maxRange = Math.max(maxDx, maxDy) * 1.1;
        const scale = Math.min(w - padding * 2, h - padding * 2) / (maxRange * 2) * zoomRef.current;
        const dx = (lon - centerLon) * Math.PI / 180 * R * Math.cos(centerLatRad);
        const dy = (lat - centerLat) * Math.PI / 180 * R;
        const x = w / 2 + dx * scale + panRef.current.x;
        const y = h / 2 - dy * scale + panRef.current.y;
        return { x, y, scale };
      };
    };
    let project = projectFactory();

    const draw = () => {
      ensureSize();
      const circuit = circuitRef.current;
      const w = canvas.width / dpr;
      const h = canvas.height / dpr;
      ctx.clearRect(0, 0, w, h);
      ctx.fillStyle = '#0a0a0a';
      ctx.fillRect(0, 0, w, h);

      if (circuit?.sectors?.length) {
        if (!project) project = projectFactory();

        const widthMeters = circuit.params?.widthMeters ?? 6;
        const pts = circuit.sectors.map(s => project(s.lat, s.lon, w, h));
        const scale = pts[0]?.scale || 1;
        const trackPx = Math.max(6, widthMeters * scale);

        ctx.strokeStyle = 'rgba(255,255,255,0.10)';
        ctx.lineWidth = trackPx + 4;
        ctx.lineCap = 'round';
        ctx.lineJoin = 'round';
        ctx.beginPath();
        pts.forEach((p, i) => i ? ctx.lineTo(p.x, p.y) : ctx.moveTo(p.x, p.y));
        ctx.closePath(); ctx.stroke();

        ctx.strokeStyle = 'rgba(80, 84, 90, 0.95)';
        ctx.lineWidth = trackPx;
        ctx.beginPath();
        pts.forEach((p, i) => i ? ctx.lineTo(p.x, p.y) : ctx.moveTo(p.x, p.y));
        ctx.closePath(); ctx.stroke();

        if (pts.length > 1) {
          const s0 = pts[0], s1 = pts[1];
          const ang = Math.atan2(s1.y - s0.y, s1.x - s0.x);
          ctx.save(); ctx.translate(s0.x, s0.y); ctx.rotate(ang);
          ctx.strokeStyle = '#ffffff';
          ctx.lineWidth = Math.max(2, trackPx * 0.12);
          ctx.beginPath(); ctx.moveTo(0, -trackPx * 0.5); ctx.lineTo(0, trackPx * 0.5); ctx.stroke();
          ctx.restore();
        }
      }

      // markers piloti (posizioni arrivate via WS GPS)
      const board = summary?.leaderboard || [];
      Object.entries(driverPositions).forEach(([mac, pos]) => {
        const p = project ? project(pos.lat, pos.lon, w, h) : null;
        if (!p) return;
        const teamCol = pos.color || '#C0FF03';
        const r = Math.max(5, 6 * (zoomRef.current * 0.9));
        ctx.shadowColor = 'rgba(0,0,0,0.5)'; ctx.shadowBlur = 4;
        ctx.fillStyle = teamCol; ctx.beginPath(); ctx.arc(p.x, p.y, r, 0, Math.PI * 2); ctx.fill();
        ctx.shadowBlur = 0; ctx.strokeStyle = '#fff'; ctx.lineWidth = 2; ctx.stroke();
        ctx.fillStyle = '#fff'; ctx.font = 'bold 10px Roboto, sans-serif'; ctx.textAlign = 'center';
        ctx.fillText((pos.tag || 'PIL').slice(0, 4), p.x, p.y - (r + 6));
      });

      animRef.current = requestAnimationFrame(draw);
    };
    draw();

    return () => {
      window.removeEventListener('resize', onResize);
      canvas.removeEventListener('wheel', wheel);
      canvas.removeEventListener('mousedown', mousedown);
      canvas.removeEventListener('mousemove', mousemove);
      canvas.removeEventListener('mouseup', mouseup);
      canvas.removeEventListener('mouseleave', mouseup);
      if (animRef.current) cancelAnimationFrame(animRef.current);
      ws.close();
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [summary?.leaderboard]);

  // ===== controlli gara (status + sanzioni) =====
  const [penPilot, setPenPilot] = useState('');
  const [penType, setPenType] = useState('+5s');

  const sendStatus = async (s) => {
    try {
      setRaceStatus(s);
      await fetch(`${API_BASE}/api/race/status`, {
        method: 'POST', headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ status: s })
      });
    } catch { }
  };
  const sendPenalty = async () => {
    if (!penPilot) return;
    try {
      await fetch(`${API_BASE}/api/race/penalty`, {
        method: 'POST', headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ pilotId: penPilot, type: penType })
      });
      // il server aggiornerà la summary via WS; qui basta pulire
      setPenPilot('');
      setPenType('+5s');
    } catch (e) {
      console.error(e);
    }
  };

  const statusColors = useMemo(() => {
    switch (raceStatus) {
      case 'RED FLAG': return { chipBg: 'rgba(225,6,0,.15)', chipBorder: '#e10600', chipText: '#ffdede', lbBorder: '#e10600' };
      case 'YELLOW FLAG': return { chipBg: 'rgba(241,196,15,.15)', chipBorder: '#f1c40f', chipText: '#fff3c4', lbBorder: '#f1c40f' };
      case 'FINITA': return { chipBg: 'rgba(154,163,154,.15)', chipBorder: '#9aa39a', chipText: '#e0e0e0', lbBorder: '#9aa39a' };
      default: return { chipBg: 'rgba(21,193,48,.15)', chipBorder: '#15c130', chipText: '#e9ffe0', lbBorder: null };
    }
  }, [raceStatus]);

  // ===== render =====
  const circuit = serverState?.circuit;
  const board = summary?.leaderboard || [];
  const totalLaps = summary?.totalLaps || serverState?.totalLaps || 0;

  return (
    <div className="main small-top">
      {/* Top bar */}
      <div className="rs-live-topbar">
        <div className="rs-live-left">
          <span className="chip readonly" style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
            <span aria-hidden style={{
              width: 10, height: 10, borderRadius: '50%', background: '#ff4d4f',
              boxShadow: '0 0 12px rgba(255,77,79,.8)', animation: 'blink 1.2s infinite'
            }} />
            LIVE
          </span>
          <span className="chip readonly">{totalLaps} giri</span>
          <span className="chip readonly" style={{
            background: statusColors.chipBg, border: `1px solid ${statusColors.chipBorder}`,
            color: statusColors.chipText, fontWeight: 800
          }}>{raceStatus}</span>
        </div>
        <div className="rs-live-right">
          <span className="chip readonly">{circuit?.name || 'Circuito'}</span>
          {circuit?.stats?.lengthMeters && <span className="chip readonly">{circuit.stats.lengthMeters.toFixed(0)} m</span>}
          {circuit?.params?.widthMeters && <span className="chip readonly">{circuit.params.widthMeters} m larghezza</span>}
        </div>
      </div>

      {/* Griglia principale */}
      <div className="rs-live-grid">
        <div className="track-card">
          <canvas ref={canvasRef} className="track-canvas" />
        </div>

        {/* classifica laterale (server-side) */}
        <div className="leaderboard-card" style={{
          borderColor: statusColors.lbBorder ?? 'var(--line)',
          boxShadow: statusColors.lbBorder ? `0 8px 32px ${statusColors.lbBorder}33, inset 0 0 0 1px ${statusColors.lbBorder}55` : undefined
        }}>
          <div className="lb-header">
            <div className="lb-title">CLASSIFICA</div>
            <div className="lb-sub">{board.length} piloti</div>
          </div>
          <div className="lb-list">
            {board.map((d, idx) => {
              const fastest = summary?.bestLapOverall && d.bestLapTime && d.bestLapTime === summary.bestLapOverall;
              const gapText = idx === 0 ? 'LEADER' : (typeof d.gap === 'string' ? `+${d.gap}` : `+${(d.gap || 0).toFixed(2)}`);
              return (
                <div key={d.mac} className={`lb-row ${idx === 0 ? 'lb-leader' : ''}`}>
                  <div className="lb-pos">{idx + 1}</div>
                  <div className="lb-team">
                    {d.photoTeamUrl ? <img src={`${API_BASE}${d.photoTeamUrl}`} alt="" className="lb-team-logo" /> :
                      <div className="lb-team-color" style={{ background: d.color }} />}
                  </div>
                  <div className="lb-name">{(d.tag || 'PIL').slice(0, 4)}</div>
                  <div className="lb-gap">{gapText}</div>
                  <div className="lb-icons">
                    {fastest && <span className="lb-icon purple">⏱</span>}
                    {d.penaltySeconds > 0 && <span className="lb-icon" title={`${d.penaltySeconds}s penalità`}>⚠️</span>}
                  </div>
                  <div className="lb-lap"><span className="muted">Lap</span>&nbsp;{d.lapCount}/{totalLaps}</div>
                </div>
              );
            })}
          </div>
        </div>
      </div>

      {/* Classifica full-width con tempi + penalità */}
      <div className="leaderboard-card" style={{ marginTop: 12 }}>
        <div className="lb-header" style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'baseline' }}>
          <div className="lb-title">CLASSIFICA DETTAGLIATA</div>
          <div className="lb-sub">{board.length} piloti • ultimo giro e best</div>
        </div>
        <div className="lb-list">
          {board.map((d, idx) => {
            const fastest = summary?.bestLapOverall && d.bestLapTime && d.bestLapTime === summary.bestLapOverall;
            const pen = d.penaltySeconds || 0;
            return (
              <div key={`wide-${d.mac}`} className="lb-row" style={{ gridTemplateColumns: '28px 28px 1fr 90px 110px 110px 110px' }}>
                <div className="lb-pos">{idx + 1}</div>
                <div className="lb-team">
                  {d.photoTeamUrl ? <img src={`${API_BASE}${d.photoTeamUrl}`} alt="" className="lb-team-logo" /> :
                    <div className="lb-team-color" style={{ background: d.color }} />}
                </div>
                <div className="lb-name">{(d.tag || 'PIL').slice(0, 4)}</div>
                <div className="lb-gap" style={{ textAlign: 'right' }}>
                  {idx === 0 ? 'LEADER' : (typeof d.gap === 'string' ? `+${d.gap}` : `+${(d.gap || 0).toFixed(2)}`)}
                </div>
                <div className="lb-gap" title="Ultimo giro" style={{ fontWeight: 700 }}>{formatLap(d.lastLapTime)}</div>
                <div className="lb-gap" title="Miglior giro" style={{ fontWeight: 700, color: fastest ? '#b085ff' : 'inherit' }}>
                  {formatLap(d.bestLapTime)}{fastest && <span className="lb-icon purple" style={{ marginLeft: 6 }}>⏱</span>}
                </div>
                <div className="lb-gap" title="Penalità" style={{ fontWeight: 700 }}>
                  {pen > 0 ? `+${pen}s` : '—'}
                </div>
              </div>
            );
          })}
        </div>
      </div>

      {/* ===== COMANDI GARA ===== */}
      <div className="pilot-form" style={{ marginTop: 12 }}>
        <h3 className="section-title" style={{ marginBottom: 10 }}>COMANDI GARA</h3>

        {/* riga 1: stato gara */}
        <div style={{ display: 'flex', flexWrap: 'wrap', gap: 8, alignItems: 'center', marginBottom: 10 }}>
          <span className="muted">Stato:</span>
          {['IN CORSO', 'FINITA', 'RED FLAG', 'YELLOW FLAG'].map(s => (
            <button key={s} className="btn-ghost"
              onClick={() => sendStatus(s)}
              style={{
                borderColor: raceStatus === s ? 'rgba(192,255,3,0.6)' : 'var(--line)',
                boxShadow: raceStatus === s ? '0 0 0 2px rgba(192,255,3,0.12) inset' : 'none',
                fontWeight: 700
              }}>
              {s}
            </button>
          ))}
        </div>

        {/* riga 2: sanzioni */}
        <div style={{ display: 'flex', flexWrap: 'wrap', gap: 8, alignItems: 'center' }}>
          <span className="muted">Sanzione:</span>
          <select className="input" style={{ maxWidth: 260 }} value={penPilot} onChange={e => setPenPilot(e.target.value)}>
            <option value="">-- Pilota --</option>
            {(serverState?.pilots || []).map(p => (
              <option key={p.id} value={p.id}>{p.name} {p.surname} ({p.team})</option>
            ))}
          </select>
          <select className="input" style={{ maxWidth: 220 }} value={penType} onChange={e => setPenType(e.target.value)}>
            <option value="+5s">+5s</option>
            <option value="+10s">+10s</option>
            <option value="+15s">+15s</option>
            <option value="1mo avv. squalifica">1mo avv. squalifica</option>
            <option value="squalifica">squalifica</option>
          </select>
          <button className="btn-primary" onClick={sendPenalty} disabled={!penPilot}>Applica</button>

          <div style={{ marginLeft: 'auto' }}>
            <button className="btn-danger" onClick={async () => {
              await fetch(`${API_BASE}/api/race/stop`, { method: 'POST' }).catch(() => { });
              onStopRace();
            }}>Termina gara</button>
          </div>
        </div>
      </div>
    </div>
  );
}
